@-moz-document url-prefix("https://discord") {
    /* setting the transitions */
    div.sidebar-2K8pFh,
    .members-1998pB,
    [class|=membersWrap],
    div.sidebar-2K8pFh *,
    .members-1998pB *,
    [class|=membersWrap] * {
        -webkit-transition: opacity 0.3s ease-in-out, width 0.3s, background 0.3s;
        -moz-transition: opacity 0.3s ease-in-out, width 0.3s, background 0.3s;
        -ms-transition: opacity 0.3s ease-in-out, width 0.3s, background 0.3s;
        -o-transition: opacity 0.3s ease-in-out, width 0.3s, background 0.3s;
        transition: opacity 0.3s ease-in-out, width 0.3s, background 0.3s;
    }

    /* adding a delay, to avoid accidental activation */
    div.sidebar-2K8pFh:hover,
    [class|=membersWrap]:hover,
    .members-1998pB:hover,
    div.sidebar-2K8pFh:hover *,
    .members-1998pB:hover *,
    [class|=membersWrap]:hover * {
        -webkit-transition-delay: .3s;
        -moz-transition-delay: .3s;
        -ms-transition-delay: .3s;
        -o-transition-delay: .3s;
        transition-delay: .3s;
    }

    /* changing the background color and shrinking sidebars */
    div.sidebar-2K8pFh,
    .members-1998pB,
    [class|=membersWrap] {
        background: #2f3136;
        width: 40px;
    }
    [class|=membersWrap] {
        width: 10px;
        min-width: 40px;
    }

    /* expanding sidebars */
    div.sidebar-2K8pFh:hover,
    [class|=membersWrap]:hover,
    .members-1998pB:hover {
        width: 250px;
    }
    [class|=membersWrap]:hover,
    .members-1998pB:hover {
        width: 240px;
    }

    /* hiding contents of sidebars */
    div.sidebar-2K8pFh *,
    .members-1998pB *,
    [class|=membersWrap] * {
        opacity: 0;
    }

    /* revealing contents of sidebars */
    div.sidebar-2K8pFh:hover *,
    .members-1998pB:hover *,
    [class|=membersWrap]:hover * {
        opacity: 1;
    }
}

@-moz-document domain("planetminecraft.com") {
.easter_egg,.easter_egg.batty{
	transition: 1800ms cubic-bezier(.89,-1.84,.23,1.39);
	transform: rotate(18000deg) skew(6500deg, 9300deg)  translate(11px, -17px);
}
.easter_egg:hover,.easter_egg.batty:hover{
	transform: rotate3d(21, -19, 0, -73.9turn);
	filter: saturate(980000);
}
}

