data.css:
@-moz-document domain("washingtonpost.com") {
div[data-qa="paywall"],
button[data-qa="subs-offer"],
.gift-offer,
#leaderboard-wrapper,
wp-ad,
wp-ad.db-ns{
    visibility: hidden !important;
    display: none !important;
    height: 0 !important;
    width: 0 !important;
}
body {
    overflow-y: scroll !important;
}
}

@-moz-document url-prefix("https://kuna.io"), url-prefix("https://support.kuna.io"), url-prefix("https://help.kuna.io"), url-prefix("https://kuna.io/fees"), url-prefix("https://money.kuna.io/"), url-prefix("https://my.kuna.io/"), url-prefix("https://status.kuna.io/") {
html {
	background: #29343e !important;
}


* {
	color: #e9ecec;

	text-shadow: 0 0 0px #78048e00 !important;
	box-shadow: none !important;
	background-color: transparent !important;
	border-color: #4440 !important;
	border-top-color: #0c537942 !important;
	border-bottom-color: #051f2d00 !important;
	border-left-color: #051f2d00 !important;
	border-right-color: #051f2d00 !important
}
body {
	background: transparent !important
}
*:before,
*:after {
	background-color: transparent !important;
	border-color: #00605f78 !important
}

a,
a * {
	color: #e9ecec !important;
	text-decoration: none !important
}
a:hover,
a:hover *,
a:visited:hover,
a:visited:hover *,
span[onclick]:hover,
div[onclick]:hover,
[role="link"]:hover,
[role="link"]:hover *,
[role="button"]:hover *,
[role="menuitem"]:hover,
[role="menuitem"]:hover *,
.link:hover,
.link:hover * {
	color: #9ccbf7 !important;
}
a:visited,
a:visited * {
	color: #e9ecec !important
}
a.highlight,
a.highlight *,
a.active,
a.active *,
.selected,
.selected *,
[href="#"] {
	color: #e9ecec!important;
}

h1,
h2,
h3,
h4,
h5,
h6,
h1 *,
h2 *,
h3 *,
strong,
[id*="headline"],
[class*="headline"],
[id*="header"],
[class*="header"],
[class*="header"] td {
	color: #81a7ca !important
}
a h1,
a h2,
a h3,
a h4,
a h5,
a h6,
h1 a,
h2 a,
h3 a,
a strong,
a[id*="headline"],
a[class*="headline"],
a[id*="header"],
a[class*="header"] {
	text-decoration: underline !important
}
[class*="error"],
[class*="alert"],
code,
span[onclick],
div[onclick] {
	color: #b5d914 !important
}

::-moz-selection {
	background: #377 !important;
	color: #d9ce08 !important
}
::selection {
	background: #377 !important;
	color: #d9ce08 !important
}
:focus {
	outline: none !important
}


/*----- MENU & CO BACKGROUND-COLORS -----*/
div[style="display: block;"],
div[role="navigation"] {
	background: rgba(0, 0, 0, .5) !important
}

table {

	border-radius: 6px !important
}
table > tbody > tr:nth-child(even),
table > tbody > tr > td:nth-child(even) {
	background-color: rgba(0, 0, 0, .24) !important
}

iframe,
embed,
nav,
label [onclick],
nav ul,
div[style*="position:"][style*="left:"][style*="visible"],
div[style*="z-index:"][style*="left:"][style*="visible"],
div[style*="-moz-user-select"],
div[role="menu"],
div[role="dialog"],
span[class*="script"] div,
[id*="menu"],
[id*="Menu"],
[class*="dropdown"],
[class*="popup"],
[class="title"],
ul[style*="display:"],
ul[style*="visibility:"] ul,
[id*="nav"] ul,
[class*="nav"] ul,
ul[class*="menu"],
a[onclick][style*="display"],
a[id*="ghosteryfirefox"],
#ghostery-purple-bubble,
#translator-popup,
.menu,
.tooltip,
.hovercard,
.vbmenu_popup {
	background: rgba(1, 25, 32, 0) !important;
	border-radius: 0px;
	box-shadow: 10px 100px 100px #0497e60d !important
}
header,
#header,
footer,
#footer {
	background: #14171a !important;
	box-shadow: 0 0 0px #082e31 !important
}
body > #dialog,
body > .xenOverlay {
	background: rgba(19, 19, 19, .96) !important;
	background-clip: padding-box !important;
	box-shadow: 0 0 15px #000, inset 0 0 0 1px rgba(200, 200, 200, .5), inset 0 0 5px #111 !important;
	border: 10px solid rgba(99, 99, 99, .7) !important;
	border-radius: 0 !important;
}
[id*="overlay"],
[id*="lightbox"],
blockquote {
	background-color: rgba(35, 35, 35, .9) !important;
	border-radius: 5px
}
pre,
dl,
.Message code {
	background-color: rgba(5, 5, 5, .5) !important
}


/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/
input,
select,
button,
[role="button"],
a.button,
a.submit,
a.BigButton,
a.TabLink,
.install[onclick] {
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	transition: border-color 0.3s !important;
	background: #00000059 !important;
	color: #fff !important;
	text-shadow: 0 0px #000 !important;
	border: 1px solid #27a7ac24 !important;
	border-radius: 3px !important;
	box-shadow: 0 0 0px rgba(0, 0, 0, .9) !important
}
a[href="javascript:;"],
a[class*="button"]:not(:empty),
a[id*="button"]:not(:empty),
a[id*="Button"]:not(:empty),
div[class*="button"][onclick] {
	transition: border-color 0.3s !important;
	background: #060606 !important;
	color: #fff !important;
	text-shadow: 0 0px #0000 !important;
	border-color: #c216ca !important;
	box-shadow: 0 0 0px rgba(0, 0, 0, .9) !important
}
a[href="javascript:;"]:hover,
a[class*="button"]:not(:empty):hover,
a[id*="button"]:hover,
a[id*="Button"]:not(:empty):hover,
div[class*="button"][onclick]:hover {
	background: #151515 !important;
	color: #FFF !important
}
input *,
select *,
button *,
a.button *,
a.submit * {
	color: #fff !important;
	text-shadow: none !important
}
input:hover,
input[type="button"]:hover,
select:hover,
button:hover,
[role="button"]:hover,
a.button:hover,
a.submit:hover,
a.BigButton:hover,
a.TabLink:hover {
	border: 1px solid #0c97b094 !important;
	border-top-color: #0c97b094 !important;
	border-bottom-color: #0c97b094 !important;
	border-left-color: #0c97b094 !important;
	border-right-color: #0c97b094 !important
}
input:focus,
select:focus {
	box-shadow: 0 0 0px #0770 !important
}
input *:hover * {
	color: #F0F0F0 !important;
	text-shadow: 0 0 0px #FFF !important
}
input[disabled],
select[disabled],
button[disabled],
input[disabled]:hover,
select[disabled]:hover,
button[disabled]:hover,
input[disabled]:focus,
select[disabled]:focus,
button[disabled]:focus {
	opacity: 0.5 !important;
	border-color: #333 !important
}

