input[type="checkbox"] {
	border-radius: 1px !important
}
input[type="radio"],
input[type="radio"]:focus {
	border-radius: 100% !important
}
input[type="checkbox"],
input[type="radio"] {
	min-width: 12px;
	min-height: 12px
}
input[type="checkbox"]:checked,
input[type="radio"]:checked {
	border-color: #077 !important;
	box-shadow: 0 0 5px #077 !important
}

select {
	padding-right: 15px !important;
	background-image: url("https://i.ibb.co/fGffmbK/wood-texture532.jpg") !important;
	transition: border-color 0.3s, background-position 0.3s !important
}


button:active,
input[type="submit"]:active,
input[type="button"]:active,
a.button:active,
a[class*="button"]:not(:empty):active,
a.submit:active,
a.BigButton:active,
