@-moz-document domain("geocaching.com"), domain("geocheck.org"), domain("geochecker.com") {
/* -=- Root Colors -=- */
:root {
    --background: #181818;
    --background-2: #202020;
    --background-3: #303030;
    --background-4: black;
    --background-alert: #0c1827;
    --foreground: #ccc;
    --foreground-2: white;
    --foreground-disabled: #828282;
    --main: #02874d;
    --main-dark: #0a1f15;
    --error: #ec0404;
    --warn: #baba03;
    --link: #3d76c5;
    --link-visited: #a565e2;
    --border: #969696;
    --border-input: 1px solid #696969;
    --table-header: #3b311b;
    --table-border: #696969;
    --table-background: #292929;
}

input,
textarea,
.gc-textfield input,
.gc-textfield textarea,
input[type="number"],
input[type="password"],
input[type="text"],
textarea.decorated,
input[type="email"],
.selectize-input,
.selectize-control .selectize-input,
.selectize-control.single .selectize-input.input-active {
    color: var(--foreground);
    background-color: var(--background);
    border: var(--border-input);
    border-radius: 4px;
}

input[type="number"][disabled],
input[type="password"][disabled],
input[type="text"][disabled],
textarea[disabled].decorated,
input[type="email"][disabled] {
    background-color: var(--background-2);
    color: var(--foreground-disabled);
}

button,
select {
    background-color: var(--background);
    color: var(--foreground);
}
}

