@keyframes rainbow {
    0% {background-color: red;border-color: red;}
    14% {background-color: blue;border-color: blue;}
    28% {background-color: yellow;border-color: yellow;}
    42% {background-color: green;border-color: green;}
    56% {background-color: red;border-color: red;}
    80% {background-color: blue;border-color: blue;}
    94% {background-color: yellow;border-color: yellow;}
    100% {background-color: green;border-color: green;}
}
