@-moz-document url-prefix("https://dist.mveu.ru") {
body {
        background: #333;
        color: #fff;
    }
    a, .disciplinesItemToggle {
        color: #68b;
    }
    #menu {
        border: none !important;
        background: #444;
    }
    #content {
        margin-left: 100px;
        margin-top: 20px;
        float: left;
        margin-right: auto;
    }
    #status_bar {
        border: none !important;
        width: calc(100% - 60px);
        height: 60px;
        background: #383838;
    }
    #status_bar > a, #status_bar > img {
        display: none;
    }
    .widget {
        background: #222 !important;
        margin: 4px;
        border: none !important;
    }
    .widget:hover {
        background: #181818 !important;
    }
    .bubble_0 {
        background: #666;
    }
    .bubble_1 {
        background: #289
    }
    .top_icon {
        margin-top: 10px;
    }
    .user_avatar {
        width: 40px;
    }
    .top-avatar {
        margin-top: 10px !important;
        width: auto !important;
    }
    .top-user-info {
        margin-top: 10px !important;
    }
    .gtitle {
        background: #444;
        padding: 20px 10px;
    }
    .carousel {
        border: none !important;
        border-radius: 4px;
        background: #666 !important;
    }
    .carousel .arrow {
        border: none !important;
        border-radius: 4px;
        background: #444 !important;
        color: #ddd;
        width: 50px;
        height: 210px;
        top: 0;
    }
    .carousel .next {
        right: 0;
    }
    .carousel .prev {
        left: 0;
    }
    .form-tabs li {
        box-shadow: none !important;
        border: none !important;
        background: none !important;
        border-bottom: none !important;
    }
    .form-tabs {
        border-bottom: 1px solid #666;
    }
    .form-tabs li:hover {
        border-bottom: 3px solid gray !important;
    }
    .form-tabs li.active {
        border-bottom: 3px solid #8af !important;
        background: none !important;
    }
    .text_bubble {
        padding: 2px 6px;
    }
    .atype {
        background: #444 !important;
    }
    .wpb_wrapper {
        background: #444 !important;
        border-radius: 4px;
    }
    .stitle {
        background: #555 !important;
        border-radius: 4px;
    }
    .wtitle > td, .gtitle {
        background: #555 !important;
    }
    .empty_week_cell, .day_cell {
        border: 2px solid #0000 !important;
        background: #444 !important;
    }
    .day_cell_active, .munread {
        background: #444 !important;
    }
    .msg_text {
        border-bottom: 1px solid #222;
    }
    .msg_text:hover {
        background: #383838;
    }
    #contact_cell {
        border-left: 1px solid #444;
    }

    #send_text {
        width: 100% !important;
    }
    #whistory tr:hover {
        background: #444 !important;
    }
    ul.documents a.doc-file {
        color: #ddd !important;
    }


}

