    }
    .CoursesItem .tags li,
    .CoursesItem .code,
    .menubar ul > li > ul,
    .toc,
    .proof .well {
        background: var(--dark-bg-3);
        color: var(--text-2)
    }
    .course-semester ul {
        border-top: 1em solid var(--dark-bg-3);
    }
    .App.in-search .CoursesItem .code,
    .App.in-search .CoursesItem:hover .code {
