data.css:
@-moz-document domain("share.dmhy.org") {
hr {
	height: 1px;
	border: none;
	border-top: 1px dashed #00000042;
}
}

@-moz-document domain("share.dmhy.org") {
.headerright,
.inner,
.footer,
.clear > div[style="width: 120px;float: left;padding-bottom: 3px;"],
[id^="kiwi-ad"],
[class^="kiwi-ad"],
ins {
	display: none !important;
}
a[href="http://www.pixivbook.com/"],
[id="1280_adv"],#bebiv3_ad{
	
	display: none !important;
}
.ui-corner-all {
	border-radius: 0 !important;
}
body,
.header {
	background: #FFF;
}


/*
	头部主体
*/
.bg {
	width: 1300px;
	margin: 0 auto;
	background: rgba(0, 0, 0, 0);
}


/*
	回首頁
*/
.headerleft img {
	width: 1300px;
	height: 665px;
	opacity: 0;
}


/*
	导航条主体部分
*/
.container > .bg > .header {
	height: 450px;
	margin: 0 0 20px 0;
	overflow: hidden;
	/*box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);*/
}
.top_sort {
	position: absolute;
	height: 35px;
	width: 1300px;
	margin: 420px 0 0 0;
	background: #0000;
	border: none;
	box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);
}
.top_sort .menu {
	width: 100%;
	height: auto;
	background: rgba(0, 0, 0, 0);
}
.top_sort .menu ul {
	line-height: 35px;
}
.top_sort > .menu > ul > li {
	width: 12.5%;
	height: auto;
}
.top_sort .menu ul li a {
	font-size: 14px;
	color: #FFF !important;
	background: rgba(0, 0, 0, .4);
}
.top_sort .menu ul li a font {
	color: #FFF !important;
}
.top_sort .menu ul li a:hover {
	background: rgba(0, 0, 0, .5);
}
.top_sort .menu ul li a:active {
	background: rgba(0, 0, 0, .6);
}
.top_sort > .menu > ul > li > ul {
	display: block;
	position: absolute;
	visibility: hidden;
	max-height: 0;
	overflow: hidden;
	transition: .2s;
	width: 100%;
	bottom: 35px;
	left: 0;
	box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);
	opacity: 0.5;
}
.top_sort > .menu > ul > li:hover > ul {
	visibility: visible;
	max-height: calc(35px * 5);
	opacity: 1;
}
.top_sort > .menu > ul > li > ul li a {
	background: rgba(0, 0, 0, .4) !important;
	transition: 0s;
}
.top_sort > .menu > ul > li > ul li a:hover {
	background: rgba(0, 0, 0, .5) !important;
}
.top_sort > .menu > ul > li > ul li a:active {
	background: rgba(0, 0, 0, .6) !important;
}
.top_sort > .menu > ul > li > ul,
.top_sort > .menu > ul > li > ul > li {
	width: 100% !important;
}


/* 字幕組選單 */
#expand-button {
	display: none;
}


/* 搜尋 */
.quick_search {
	position: absolute;
	width: 1300px;
	height: 35px;
	top: 1px;
	margin: 1px 0 0 0;
	padding: 0;
	border: 0;
	border: none;
	background: #0000;
	box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);
}
.quick_search input {
	float: left;
}
.quick_search .quick_input {
	width: 380px;
	height: 35px;
	margin: 0;
	border: 0;
	padding: 0 12px 0 12px;
	font-size: 14px;
	color: #FFF;
	outline: 0;
	background: rgba(0, 0, 0, 0.27);
}
.formButton {
	width: 90px;
	height: 35px;
	margin: 0;
	border: 0;
	background: rgba(240, 57, 76, 0.7);
	cursor: pointer;
	font-size: 14px;
	color: #FFF;
}
.formButton:hover {
	background: rgba(240, 57, 76, 0.85);
}
.quick_search > form > a {
	float: left;
	width: 90px;
	height: 35px;
	margin: 0 -2px 0 0;
	border: 0;
	background: rgba(0, 0, 0, 0);
	cursor: pointer;
	font-size: 14px;
	line-height: 37px;
	color: #FFF;
}
.quick_search > form > a:hover {
	text-decoration: none;
	background: rgba(0, 0, 0, 0.2);
}
.quick_search > form > a > span {
	display: none;
}
#AdvSearch {
	margin: -8px 0 0 0;
	font-size: 12px;
	/*height: 35px!important;
    margin-top: -14px!important;*/
	padding-top: 0px;
	/*background-color: #00000045;*/
	/*line-height: 35px;*/
	/*display: block !important;*/
}
#AdvSearch p {
	color: #FFF;
	margin: 0;
}
#AdvSearch p:nth-child(4) {
	display: none;
}
.main {
	padding: 0;
	background: rgba(0, 0, 0, 0);
	margin-bottom: 20px;
}


/*
	每日新番框
*/
#mini_jmd {
	width: 1298px !important;
	margin: 0 0 20px 0;
	padding: 0;
	background: rgba(255, 255, 255, 0.68);
	box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);
}
#mini_jmd > .nav_title {
	height: 20px;
	background: rgba(44, 62, 80, .6);
	color: #FFF;
	font-size: 14px;
	line-height: 16px;
	padding: 10px 0 4px 13px;
}
#mini_jmd > .nav_title > a {
	color: #FFF;
}
#mini_jmd > .nav_title > span {
	display: none;
}
.jmd_base {
	padding: 10px 0 10px 0;
	border-spacing: 3px 0;
	background-color: #ffffffc4;
}
.jmd_base > tbody > tr {
	background: rgba(0, 0, 0, 0);
}
.jmd_base > tbody > tr > th {
	width: 100px;
}
.jmd_base > tbody > tr > td > a {
	margin: 3px;
	padding: 8px;
	font-size: 12px;
	color: #FFF;
	display: inline-block!important;
	background: #3E5368;
}
.jmd_base > tbody > tr > td > a:hover {
	text-decoration: none;
	background: #2C3E50;
	transform: scale(1.05);
	transition: .1s;
	box-shadow: 3px 3px 10px rgba(0, 0, 0, .2);
	transition-timing-function: cubic-bezier(0.42, 0, 0.58, 1.79);
}
.jmd_base > tbody > tr > td > a:active {
	text-decoration: none;
	background: #3069a1;
}


/*开启全部日期*/
.jmd {
	display: none!important;
}
.jmd_base {
	display: block!important;
}


/*
	番剧列表
*/
.table {
	width: 1298px !important;
	margin: 0 0 10px 0;
	padding: 0;
	background: rgba(255, 255, 255, 0);
	border: 0;
	box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);
	border-spacing: 0px!important;
}
.clear > .nav_title {
	height: 20px;
	background: rgba(45, 62, 80, 0.6);
	color: #FFF !important;
	font-size: 14px;
	line-height: 16px;
	padding: 10px 13px 4px 13px;
}
.clear > .nav_title > a {
	color: #FFF;
}
.clear > .nav_title:first-child {
	display: flex;
	flex-wrap: wrap;
}
.clear > .nav_title:first-child > .fl {
	flex: 1;
}
.clear > .nav_title:first-child > .fl a,
.clear > .nav_title:last-child a {
	background: #00000080;
	padding: 2px 5px;
	border-radius: 2px;
	text-decoration: none;
}
.clear > .nav_title:first-child > .fl a:hover,
.clear > .nav_title:last-child a:hover {
	background: #000000ad;
}
.clear > .nav_title:first-child > .fl a:active,
.clear > .nav_title:last-child a:active {
	background: #000000d6;
	color: #a8a8a8;
}
.clear > table {
	border-spacing: 0px;
}
.table > .nav_title > .fl > a,
.table > .nav_title > .fr > a {
	color: #FFF;
}
.table > .nav_title > .fr > a:nth-child(3) > img {
	display: none !important;
}
.tablesorter > thead > tr > th {
	height: 20px;
	font-size: 14px;
	background: rgba(52, 73, 94, .6);
	transition: .1s;
	transition-timing-function: cubic-bezier(0.42, 0, 0.58, 1.79);
}
.tablesorter > thead > tr > th:hover {
	background: rgba(52, 73, 94, .7);
}
.even > td {
	background: rgba(0, 0, 0, 0) !important;
}
table.tablesorter tbody td {
	padding: 9px 8px 9px 8px;
}
table#topic_list,
#resource-tabs,
.topic-main {
	text-shadow: 0px 0px 5px rgba(255, 255, 255, 0.74);
}
table.tablesorter,
.list_style {
	background-color: rgba(255, 255, 255, 0);
}
.odd,
.list_style .odd {
	background-color: rgba(243, 243, 243, 0.65);
}
.even,
.list_style .even {
	background-color: rgba(255, 255, 255, 0.7);
}
table#topic_list tr:hover {
	transform: scale(1.01);
	transition: .1s;
	box-shadow: 3px 3px 10px rgba(0, 0, 0, .2);
}


/* 字幕組標籤 */
table#topic_list tr td span.tag {
	padding: 0px;
	border: none;
}
table#topic_list tr td span.tag > a {
	background: #546E7A;
	color: #fff;
	padding: 4px;
	text-shadow: none;
	border: 1px solid transparent;
	transition: .1s;
}
table#topic_list tr td span.tag > a:hover {
	box-shadow: inset 0 0 0px 20px #0000003b;
}
table#topic_list tr td span.tag > a:active {
	box-shadow: inset 0 0 0px 20px #00000061;
}


/*
	底部提醒
*/
.table > .content {
	background-color: rgba(243, 243, 243, 0.65);
	font-size: 15px;
	padding: 10px 5px 20px;
}
.nav_title {
	height: 30px;
	font-size: 20px;
	padding: 5px 10px 0px;
	background: rgba(45, 62, 80, 0.6);
}


/* 
	卷軸樣式
*/
::-webkit-scrollbar-track {
	background-color: #F5F5F5;
}
::-webkit-scrollbar {
	width: 5px;
	background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb {
	box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
	background-color: #555;
}


/*
	Footer
*/
.bg > div[style="text-align: center; font: 11px arial; color: #AAA; clear: both;"],
.bg > div[style="text-align: center; font: 11px arial; color: #AAA; clear: both;margin-bottom: 10px;"] {
	display: none;
}
}

@-moz-document url-prefix("https://share.dmhy.org/topics/view/"), url-prefix("http://share.dmhy.org/topics/view/") {
#mini_jmd,
.user-sidebar,
.clear > div[style="width: 120px;float: left;padding-bottom: 3px;"] {
	display: none;
}
.topics_bk {
	padding: 0;
	border: 0;
	margin: 0;
	background: rgba(0, 0, 0, 0);
}


/*发布信息*/
.topic-main > .box:nth-child(1) {
	width: 1298px;
	height: 357px;
	margin: 0;
	padding: 0px;
	background: rgba(255, 255, 255, 0.5);
	border: none;
	box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);
}
.topic-title h3 {
	height: 20px;
	background: rgba(44, 62, 80, 0.6);
	color: #FFF;
	font-size: 14px;
	line-height: 16px;
	padding: 10px 0 4px 13px;
	white-space: pre;
	overflow: hidden;
	border-bottom: none;
}
.topic-main > .box:nth-child(1) > div {
	margin: 0 0 0 10px;
	float: left;
	text-shadow: 0 0 0 black;
}
.topic-main > .box:nth-child(1) > div > ul {
	margin: 0;
}
.topic-main > .box:nth-child(1) > div > ul > li {
	margin: 10px;
	font-size: 13px;
}
.topic-main > .box:nth-child(1) > div > ul > li:nth-child(4),
.topic-main > .box:nth-child(1) > div > ul > li:nth-child(6) {
	display: none;
}
.topic-main .share {
	display: none;
}


/*下载列表*/
.topic-main .resource-detail {
	position: absolute;
	width: 900px;
	top: -1px;
	margin: 511px 0 0 220px;
	border: 0;
	background: rgba(0, 0, 0, 0);
}
.ui-tabs .ui-tabs-nav {
	display: none;
}
.ui-tabs .ui-tabs-panel {
	padding: 0;
	border: 0;
}
.ui-tabs .ui-tabs-panel p {
	margin: 10px;
	overflow: hidden;
	white-space: nowrap;
	text-overflow: ellipsis;
}
.ui-tabs .ui-tabs-panel hr {
	display: none;
}
.file_list {
	position: absolute;
	top: -1px;
	width: 1254px;
	height: 144px;
	margin: 165px 0 0 -207px;
	padding: 0;
	background: rgba(255, 255, 255, 0.2);
	border: 1px solid #C6C6C6;
}
.file_list ul li {
	padding: 8px;
}
.bt_file_size {
	color: #4e4e4e;
	font-size: 12px;
	float: right;
	line-height: 17.5px;
}
.even {
	background-color: rgba(255, 255, 255, 0.31) !important;
}
.warn {
	display: none !important;
}


/*文件信息*/
.topic-main {
	width: 100%;
}
.topic-main > .box:nth-child(2) {
	padding: 10px;
	margin: 10px 0 0px 0;
	border-radius: 0;
	background: rgba(255, 255, 255, 0.5);
	box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);
	border: none;
}
.topic-main > .box:nth-child(2) p {
	font-size: 18px;
}
.topic-main > .box:nth-child(2) img {
	display: block;
	height: auto !important;
	width: 30%!important;
	margin: 0 auto;
}
.topic-nfo.box.ui-corner-all > strong:nth-child(1) {
	font-size: 0;
}
.topic-nfo.box.ui-corner-all > strong:nth-child(1):before {
	display: block;
	content: '簡介';
	height: 20px;
	background: rgba(44, 62, 80, 0.6);
	color: #FFF;
	font-size: 14px;
	line-height: 16px;
	padding: 10px 0 4px 13px;
	white-space: pre;
	overflow: hidden;
	margin: -10px -10px 10px -10px;
}


/*
	留言
*/
.comment_recent_main.ui-corner-all:before {
	display: block;
	content: '留言';
	height: 20px;
	background: rgba(44, 62, 80, 0.6);
	color: #FFF;
	font-size: 14px;
	line-height: 16px;
	padding: 10px 0 4px 13px;
	white-space: pre;
	overflow: hidden;
}
#recent-commnet {
	margin: 0;
	margin-top: 10px;
	padding: 0;
	border: none;
	width: 100%;
	background: #0000;
	min-height: 2px;
	box-shadow: 0 2px 11px rgba(0, 0, 0, 0.38);
}
#recent-commnet .declaration {
	display: none;
}
#comment_recent {
	background: #0000;
}
#comment_recent tr,
#topic_add_form {
	background: #fff6;
}
#comment_recent .infotable {
	background: #fff6;
}
#comment_recent .comment_con {
	background: #fff9;
}
table {
	border: none;
	border-collapse: collapse;
}


/*標題*/
#comment_recent .comment_con .title {
	border-bottom: 1px solid #888;
}


/*新增留言*/
#comment_add {
	margin: 0;
	background: #0000;
	border-radius: 0 !important;
}
#comment_add > table {
	border-radius: 0 !important;
}
#comment_add > table > tbody > tr:nth-child(2) {
	background: RGBA(255, 255, 255, 0.7);
}
.add_advert {
	background: RGBA(255, 255, 255, 0.3);
	border-left: none;
}
.add_title {
	font-size: 14px;
	line-height: 16px;
	height: 35px;
	padding: 0px 0 0px 13px;
	border-radius: 0 !important;
	font-weight: normal;
	background: rgba(44, 62, 80, 0.6);
	width: auto;
	color: #FFF;
}
.comment_add_table th,
.comment_add_table td {
	border-bottom: none;
	border-left: none;
}
.comment_add_table th {
	font-size: 13px;
	font-weight: normal;
	line-height: 1.12em;
	;
	color: rgb(83, 83, 83);
}
#comment_add input[type=text],
textarea {
	border: 1px solid #DDDDDD;
	padding: .78571em 1em;
	font-size: 13px;
	line-height: 1.2142em;
	background-color: #FFF6;
}
#comment_content {
	max-width: 795px;
}
#comment_add input[type=text]:focus,
textarea:focus {
	border: 1px solid #b9c6ff;
}
img.user_avatar_img {
	border: 1px solid #cdf;
	border-radius: 5px;
}
}

@-moz-document domain("share.dmhy.org") {
/*
	RWD
*/
@media (max-width: 1350px) {
	.top_sort,
	.quick_search,
	.top_sort .menu,
	.table,
	.container > .bg > .header,
	#mini_jmd {
		width: 940px !important;
	}
	.bg,
	.main,
	.container {
		width: 950px;
		min-width: 700px;
		margin: auto;
	}
	.top_sort {
		height: 70px;
	}
	.main {
		margin-top: 10px;
	}
	.container > .bg > .header {
		height: 478px;
	}
	/* NAV */
	.top_sort .menu > ul > li {
		width: 25%;
	}
	.top_sort .menu > ul > li > ul > li {
		width: 100%;
	}
	.top_sort .menu ul li:hover ul {
		width: 100%;
	}
	/* 搜尋 */
	.quick_search {
		height: auto;
	}
	#AdvSearch {
		margin: 21px 0 0 0;
		padding: 6px;
		display: none;
	}
	#AdvSearch p {
		display: table-cell;
	}
	.quick_search .quick_input {
		width: 485px;
	}
	/* 隱藏清單的下載量發布人啥的 */
	table > tbody > tr > td:nth-child(6),
	table > tbody > tr > td:nth-child(7),
	table > tbody > tr > td:nth-child(8),
	table > tbody > tr > td:nth-child(9),
	table > thead > tr > th:nth-child(6),
	table > thead > tr > th:nth-child(7),
	table > thead > tr > th:nth-child(8),
	table > thead > tr > th:nth-child(9),
	#team-expand,
	#team-collapse {
		display: none !important;
	}
	table.tablesorter > tbody > tr > td:nth-child(2),
	table > thead > tr > th:nth-child(2) {
		width: 7%;
	}
}
@media (max-width: 960px) {
	.top_sort,
	.quick_search,
	.top_sort .menu,
	.table,
	.container > .bg > .header,
	#mini_jmd {
		width: 840px !important;
	}
	.bg,
	.main,
	.container {
		width: 850px;
		min-width: 700px;
		margin: auto;
	}
}
@media (max-width: 860px) {
	.top_sort,
	.quick_search,
	.top_sort .menu,
	.table,
	.container > .bg > .header,
	#mini_jmd {
		width: 690px !important;
	}
	.bg,
	.main,
	.container {
		width: 700px;
		min-width: 700px;
		margin: auto;
	}
}
}

