@-moz-document domain("share.dmhy.org") {
/*
	字幕組顏色
*/
table#topic_list tr td span.tag > a[href$="2"] {
	background: #e53935;
}
table#topic_list tr td span.tag > a[href$="3"] {
	background: #D81B60;
}
table#topic_list tr td span.tag > a[href$="4"] {
	background: #8E24AA;
}
table#topic_list tr td span.tag > a[href$="5"] {
	background: #3949AB;
}
table#topic_list tr td span.tag > a[href$="6"] {
	background: #00ACC1;
}
table#topic_list tr td span.tag > a[href$="7"] {
	background: #43A047;
}
table#topic_list tr td span.tag > a[href$="8"] {
	background: #C0CA33;
}
table#topic_list tr td span.tag > a[href$="9"] {
	background: #FB8C00;
}
table#topic_list tr td span.tag > a[href$="0"] {
	background: #6D4C41;
}
