}

@-moz-document domain("ogame.gameforge.com") {
body {
    background: none !important;
    background-color: #000000 !important;
    background: linear-gradient(to bottom, rgba(0, 0, 0, 1.0), rgba(0, 0, 0, 1.0), rgba(38, 38, 38, 1.0)) !important;
    background-attachment: fixed !important;
}

#mmonetbar,
div#banner_skyscraper a,
#banner_skyscraper,
#promotionCountdownBox {
    display: none !important;
}
#top{
     top: 0px !important;
    left: 25% !important;
background: radial-gradient(ellipse at center, #262626 0%, transparent 100%) no-repeat !important;
    border-radius: 0px 0px 15px 15px !important;
    width: 1000px !important;
}
/**********PANEL DINFORMATION*************/


