    left: 240px !important;
}



.menubutton {
    background: none !important;
}
#detail,
#station #jumpgate #jumpgateNotReady,
#station-moon #jumpgate #jumpgateNotReady {
    background: -moz-linear-gradient(-90deg, rgba(38, 38, 38, 1.0), rgba(0, 0, 0, 1.0), rgba(38, 38, 38, 1.0)) !important;
    background: -webkit-linear-gradient(-90deg, rgba(38, 38, 38, 1.0), rgba(0, 0, 0, 1.0), rgba(38, 38, 38, 1.0)) !important;
    background: -o-linear-gradient(-90deg, rgba(38, 38, 38, 1.0), rgba(0, 0, 0, 1.0), rgba(38, 38, 38, 1.0)) !important;
}

/*******************PANEL DINFO***********************/
    #ago_panel {
