}

#planetdata td.desc {
    color: #797979 !important;
}
.ago_improve div#planetDetails {
    position: relative;
    right: 22px;
}

/********ABANDONNER RENOMMER***********/
.planetMoveOverviewGivUpLink,
span.planetMoveIcons:nth-child(2) {
    position: relative;
    top: -40px;
}
/******VUE_PLAN7TE***********/
#inhalt {
    position: relative !important;
    top: 30px !important;
}
/**************INFO_COMPTE*************************/
