    font-family: 'Sonsie One', cursive !important;
    font-size: 8px !important;
}

#IFC_table {
    padding-top: 30px !important;
}
#IFC_top {
    background: -moz-linear-gradient(center top, #4e4e4e 20%, #101419 100%) !important;
    background: -webkit-linear-gradient(top, #4e4e4e 20%, #101419 100%) !important;
    border-radius: 15px 15px 0px 0px !important;
}
.IFC_th {
    color: #6e6e6e !important;
    background: none !important;
}

table[style="width:675px; clear:right;"] {
    position: relative !important;
    top: 100px !important;
}
th[background] {
    background: none !important;
    background: -moz-linear-gradient(center top, #4e4e4e 20%, #101419 100%) !important;
    background: -webkit-linear-gradient(top, #4e4e4e 20%, #101419 100%) !important;
    border-radius: 15px 15px 0px 0px !important;
}

/******************/
