    background: -moz-linear-gradient(-90deg, rgba(255, 255, 255, 0.2), rgba(0, 0, 0, 0.2), rgba(38, 38, 38, 0.2)) !important;
    background: -webkit-linear-gradient(-90deg, rgba(255, 255, 255, 0.2), rgba(0, 0, 0, 0.2), rgba(38, 38, 38, 0.2)) !important;
    border-radius: 8px 8px 0px 0px !important;
}
#buffBar:hover {
