    background: -webkit-linear-gradient(-90deg, rgba(255, 255, 255, 0.1), rgba(0, 0, 0, 0.4), rgba(38, 38, 38, 0.4)) !important;
    border-radius: 8px 8px 0px 0px !important;
}
/***************************MENU PLANETE DETAILS CONSTRUCTION****************/
span[class="anti_planets_construction"] {
    color: #bdffe4 !important;
}
span[class="anti_planets_constructionMoon"] {
    color: #fffebd !important;
}


/*******************ANTIGAME PILLAGE**********************************/
