}

.status_abbr_longinactive,
.status_abbr_inactive {
    text-shadow: 3px 1px 3px black !important;
}

table.anti_msg_buttons:nth-child(5) {
    width: auto !important;
}
/*******************ANTI POSITION MENU********************************/
