    position: absolute !important;
    top: 20px !important;
}
.ago .build-it_wrap {
    position: absolute !important;
    top: 24px !important;
}

.ago_color_lightgreen {
    position: relative !important;
    left: -2px !important;
}

/***********MESSAGE*************/
html body#messages.ogame.lang-fr.default.no-commander.no-touch.ago_improve div.contentBoxBody div#boxBG div#box div#contentWrapper.with_chat_bar div#content div#buttonz {
    position: relative;
    top: 20px;
}

