/*ONGLET**/
div.marker {
    background: linear-gradient(to bottom, rgba(250, 250, 250, 0.4), rgba(100, 100, 100, 0.1), rgba(10, 10, 10, 0.1)) !important;
    border-radius: 20px 20px 0px 0px !important;
}


#messages .tab_inner::before {
    display: none;
}


.subtabs .ui-tabs-active.first .txt_link,
.subtabs .ui-tabs-active.last .txt_link,
.no-touch .subtabs .ui-state-hover,
.subtabs .ui-tabs-active,
.subtabs .last .txt_link,
.subtabs .first .txt_link,
.subtabs {
    background-repeat: no-repeat!important;
    background-size: 100% 100%;
}

.tabs_wrap .tab_ctn {
    background: linear-gradient(to left, rgba(36, 36, 36, 1.0), rgba(60, 60, 60, 1.0), rgba(38, 38, 38, 1.0)) !important;
}

.subtabs {
