/* ==UserStyle==
@name           Pretty Codewars
@version        2.0.0
@namespace      github.com/goodjack/pretty-codewars
@description    Larger font size, increased line height, and the beautiful Fira Code font.
@author         goodjack <goodjack@users.noreply.github.com> (https://github.com/goodjack)
@homepageURL    https://github.com/goodjack/pretty-codewars
@supportURL     https://github.com/goodjack/pretty-codewars/issues
@updateURL      https://raw.githubusercontent.com/goodjack/pretty-codewars/master/style.user.css
@license        CC-BY-4.0
==/UserStyle== */

@-moz-document domain("codewars.com") {
  @font-face {
    font-family: "Fira Code";
    src: url("https://raw.githubusercontent.com/tonsky/FiraCode/master/distr/eot/FiraCode-Regular.eot")
        format("embedded-opentype"),
      url("https://raw.githubusercontent.com/tonsky/FiraCode/master/distr/woff2/FiraCode-Regular.woff2")
        format("woff2"),
      url("https://raw.githubusercontent.com/tonsky/FiraCode/master/distr/woff/FiraCode-Regular.woff")
        format("woff"),
      url("https://raw.githubusercontent.com/tonsky/FiraCode/master/distr/ttf/FiraCode-Regular.ttf")
        format("truetype");
    font-weight: normal;
    font-style: normal;
  }

  .CodeMirror pre.CodeMirror-line {
    font-size: 17px;
    line-height: 1.6;
    font-family: "Fira Code";
  }

  .markdown code,
  pre code,
  .result-type--log .result-type__value {
    font-family: "Fira Code";
  }
}

