@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
:root {
   
    --white1: #FFF;
    --white2: #F7F7F7;
    --white3: #DDD;
    --black1: #000;
    --black2: #111111;
    --black3: #121212;
    --black4: #131313;
    --black5: #151515;
    --black6: #161616;
    --black7: #1C1C1C;
    --black8: #252525;
    --gray1: #383838;
    --gray2: #777777;
    --gray3: #999;
    
    --customcolor1: #00ba57;
    --customcolor2: #20211f;
    --customcolor3: #FFF;
    --customcolor4: #1b1c1a;
    --customcolor1t1: rgba(253, 149, 78, 0.5);
    --customcolor1t2: rgba(253, 149, 78, 0.65);
    --customcolor2t1: rgba(27, 28, 26, 0.5);
    --customcolor2t2: rgba(27, 28, 26, 0.65);
    --customcolor4t2: rgba(20, 20, 20, 1);
   
    --backgroundcolor: #383838;
   
}

 .userbar:before {
    display: block;
    content:"";
    width: 32px;
    height: 32px;
    float: left;
    position: relative;
    top: -5px;
    background: var(--icon);
    background-repeat: no-repeat;
}

 i.fa.fa-user::before {
    content:"";
}
.icon-user {
    display: none;
}
button:focus, select:focus {
    outline: 0;
}
.body {
    background: var(--gray1);
}
.mainmenuwrapper {
    background:  url("http://i.imgur.com/7ZKIcIF.jpg") top left;
    border-right: none;
    background-size: cover;
}
.ps-overlay {
    background: rgba(0, 0, 0, 0.3);
}
.ps-room.ps-room-light {
    background-color: rgba(0, 0, 0, 0);
    border-color: var(--customcolor1);
}

/*chat*/
.ps-room.ps-room-light.scrollable {
    background: var(--gray1) center right no-repeat;
    border-color: var(--black1);
    color: var(--white1);
    text-shadow: 0px 0px 3px var(--black1);
}
.ps-room.ps-room-light.scrollable[id*="view-"] {
    background: var(--gray1);
}
button[value="/pets validateteam"], button[value="/pets viewteams"], button[value="/pets saveteam"] {
    color: var(--black1);
}
/*------------------------------------*\
  #Join Room Tab
\*------------------------------------*/
 .roomlist a.ilink, .roomcounters button {
    border-color: var(--customcolor1);
    box-shadow: 0px 0px 2px var(--customcolor1);
    background: transparent;
    color: var(--customcolor1);
    text-shadow: 0px 0px 2px var(--black3);
    transition: 0.3s;
}
.roomlist a.ilink:hover, .roomcounters button:hover {
    border-color: var(--customcolor3);
    box-shadow: 0px 0px 2px var(--customcolor3);
    background: transparent;
    color: var(--customcolor1);
    text-shadow: 0px 0px 2px var(--black3);
    transition: 0.3s;
}
button[name="finduser"], button[name="finduser"]:focus, button[name="roomlist"], button[name="roomlist"]:focus, button[name="closeHide"], button[name="closeHide"]:focus, button[name="cancelSearch"], button[name="cancelSearch"]:focus {
    border-color: var(--customcolor1)!important;
    box-shadow: 0px 0px 2px var(--customcolor1)!important;
    background: transparent!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}
button[name="finduser"]:hover, button[name="roomlist"]:hover, button[name="closeHide"]:hover, button[name="cancelSearch"]:hover {
    border-color: var(--customcolor3)!important;
    box-shadow: 0px 0px 2px var(--customcolor3)!important;
    background: var(--customcolor4t2)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}
button[name="finduser"]:active, button[name="roomlist"]:active, button[name="closeHide"]:active, button[name="cancelSearch"]:active {
    border-color: var(--customcolor3)!important;
    box-shadow: none!important;
    background: var(--customcolor4)!important;
    color: var(--customcolor1)!important;
    text-shadow: 0px 0px 2px var(--black3)!important;
    transition: 0.3s;
}
.roomlist h2 {
    color: var(--white1);
    text-shadow: 0px 0px 3px var(--black1);
}
div.newsentry.unread {
    background: var(--white3);
    color: var(--black1);
}
.menugroup {
    background: var(--customcolor2t1);
    border: 1px solid var(--customcolor1);
}
.label {
    text-shadow: 0px 0px 3px var(--black1);
}
/*------------------------------------*\
