.rc-CoverPageNudges {
  background-color: #282828;
}

.rc-CoverPageRow {
  border-bottom-color: #666;
}

.rc-CoverPageRowRightSideGrade {
  border-left-color: #666;
}

/***** Graded Quiz Page *****/

.rc-TunnelVisionWrapper .rc-TunnelVisionClose, .rc-TunnelVisionWrapper .rc-TunnelVisionClose:hover {
  background-color: #282828;
}

.rc-TunnelVisionHeader {
  border-bottom-color: #444;
}

.rc-AttemptPageTopBanner__grade-info {
  border-left-color: #444;
}

div[data-classname="tunnelvision-window-0"] {
