}

/* Questions */
legend {
  color: #ccc;
}

.rc-QuestionPoints__pill {
  border-color: #666 !important;
  color: #ccc;
}

.rc-CML.styled figure img {
  opacity: 0.6;
}

input.honor-code-agreement.name-input.pii-hide {
  background-color: #282828;
  border-color: #444;
}

.rc-SubmissionControls__button {
  background-color: #333;
}

/* Results */

.rc-GradeNotification--success {
  background-color: #333;
}

.rc-GradeNotification--partial {
  background-color: #333;
}

.rc-AttemptPageTopBanner--finished, .rc-AttemptPageTopBanner--passed {
  background-color: #2f3130; /* #f3faf7; */
}

.rc-Option.rc-Option--isReadOnly .cui-Checkbox:hover {
  color: #0156b8;
}

/******************** Course Home Page e.g. www.coursera.org/learn/neural-networks-deep-learning/home/  ********************/

/***** Left column *****/

.rc-CourseNavigation {
  background-color: #282828;
}

.rc-NavigationLink {
  color: #ccc;
}

.rc-NavigationLink:active, .rc-NavigationLink:focus, .rc-NavigationLink:hover {
  background-color: #333;
  color: #ccc;
}

.rc-NavigationDrawerLink {
  color: #aaa;
}

.rc-NavigationLink.selected {
  background-color: #333;
}

.rc-NavigationDrawerLink.selected, .rc-NavigationDrawerLink:active, .rc-NavigationDrawerLink:focus, .rc-NavigationDrawerLink:hover {
  background-color: #333;
  color: #ccc;
}

/***** Right column *****/

/**** Overview ****/

.rc-CourseNameHeader .course-name {
  color: #aaa;
}

/** Timeline **/

.rc-ItemIcon.horizontal-box svg {
  color: #f00 !important;
}

.rc-TooltipAssignmentProgressIcon .icon-wrapper {
  background-color: #333;
}

.rc-CourseMaterial {
  color: #aaa;
}

.rc-WeekCardHeader {
  background-color: #333;
}

.rc-WeekCardBody {
  background-color: #282828;
}

.module-section {
  color: #aaa;
}

.rc-UngradedItem:hover {
  background-color: #333;
}

.rc-AssignmentRow:hover {
  background-color: #333;
}

/*** Overview > Week ***/

.rc-Home .course-content {
  background-color: #282828;
}

.rc-PeriodPage .week-description .week-number {
  color: #aaa;
}

.rc-PeriodPage .week-description .course-title {
  color: #ccc;
}

.card-no-action, .card-one-clicker, .card-one-clicker:hover, .card-rich-interaction, .modal.card-one-clicker, .modal.card-one-clicker:hover, .modal.card-rich-interaction, [class*="card-"].selected, ul.styleguide.dropdown {
  background-color: #282828;
  border-color: #333;
}

.card-headline-text {
  color: #aaa;
}

.card-one-clicker:hover, .card-one-clicker:hover:hover, .card-rich-interaction:hover, .modal.card-one-clicker:hover, .modal.card-one-clicker:hover:hover, .modal.card-rich-interaction:hover, [class*="card-"].selected:hover, ul.styleguide.dropdown:hover {
  border-color: #666;
}

.rc-WeekSingleItemDisplay.highlighted, .rc-WeekSingleItemDisplay:hover {
  background-color: #333;
}

/* hovering Help widget */
.rc-Help .help-widget {
  background-color: #333;
}

/**** Resources ****/

.rc-CML.styled .cml-asset .asset-container {
  border-color: #666;
}

.rc-Home {
  background-color: #282828;
}

/**** Course Info ****/

.rc-TogglableContent.white-gradient .content .overlay {
  background: linear-gradient(transparent,transparent,#444),transparent;
}

/******************** Course Discussions Forum e.g. www.coursera.org/learn/neural-networks-deep-learning/discussions/weeks/N ********************/

/***** Middle column *****/

/** Tags **/

/* e.g. Mentor Replied */
.bgcolor-accent-brown-light {
  background-color: #3f382a; /* #ece9e3; */
}

.rc-ThreadBadge {
  color: #cb9025; /* #8a6219 */
}

/* e.g. Staff */
.bgcolor-success-light {
  background-color: #3f612f; /* #ecf4e8; */
}

.color-secondary-text {
  color: #ccc;
}

/** Posts **/

.rc-ThreadsListEntry.focused, .rc-ThreadsListEntry:hover {
  background-color: #333;
}

/* 'Most Recent' */

ol.tabs li.colored-tab.selected, ol.tabs li.tab.selected, ol.tabs li.tab:hover, ul.tabs li.colored-tab.selected, ul.tabs li.tab.selected, ul.tabs li.tab:hover {
