  background-color: #222a;
}

.search-bar .input-area .search-input {
  background-color: #333;
}

/***** Right column *****/

.rc-SubforumSidebar .subforum-entry:hover {
  background-color: #333;
}

/*** Reply Editor ***/

.rc-CMLEditor .scribe-editor {
  background-color: #333;
  border-color: #444;
}

.rc-CMLEditorToolbar {
  background-color: #282828;
  border-color: #444;
}

.rc-CMLEditorToolbar .scribe-toolbar-button:hover:not(:disabled) {
  background-color: #333;
  outline: 1px solid #444;
}

/* Reply button */
.search-bar .search-button[disabled], button.facebook[disabled], button.linkedin[disabled], button.passive[disabled], button.primary[disabled], button.secondary[disabled] {
  background-color: #333;
  border-color: #444;
  color: #aaa;
