}

.rc-CML.styled pre {
  border: 1px solid #444;
}

pre {
  background-color: #333;
}

.ace-tomorrow .ace_gutter-active-line {
  background-color: #444;
}

/******************** Course Page (without login) e.g. www.coursera.org/learn/neural-networks-deep-learning/  ********************/

/* 'Go To Course' button */
.EnrollButton button {
  background-color: #333 !important;
  color: #aaa !important;
}

/* Tags */

.border-a {
