}

#guide-section-title.ytd-guide-section-renderer {
    color: #000000;
    display: block;
    padding: 39px 24px;
    font-size: 25px;
    font-weight: var(--ytd-tab-system_-_font-weight);
    letter-spacing: 0px;
    text-transform: var(--ytd-tab-system_-_text-transform);
}

#guide-content.ytd-app {
    background: #dedede;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
}

#guide-section-title.ytd-guide-section-renderer {
    color: #000000;
    display: block;
    padding: 39px 24px;
    font-size: 25px;
    font-weight: var(--ytd-tab-system_-_font-weight);
    letter-spacing: 0px;
    text-transform: var(--ytd-tab-system_-_text-transform);
    /* border-radius: 25px; */
}

canvas, caption, center, cite, code, dd, del, dfn, div, dl, dt, em, embed, fieldset, font, form, h1, h2, h3, h4, h5, h6, hr, i, iframe, img, ins, kbd, label, legend, li, menu, object, ol, p, pre, q, s, samp, small, span, strike, strong, sub, sup, table, tbody, td, tfoot, th, thead, tr, tt, u, ul, var {
    margin: 0;
    padding: 0;
    border: 0;
    background: #ffffff00;
    border-radius: 20px;
}

#account-name.ytd-active-account-header-renderer {
    color: var(--yt-spec-text-primary);
    font-size: 30px;
    font-weight: var(--ytd-subheadline-link_-_font-weight);
    line-height: 80px;
}

element.style {
    outline: none;
    z-index: 2202;
    position: fixed;
    top: 781.5px;
    left: 588.5px;
}

img.yt-img-shadow {
    display: block;
    margin-left: var(--yt-img-margin-left, auto);
    margin-right: var(--yt-img-margin-right, auto);
    max-height: var(--yt-img-max-height, none);
    max-width: var(--yt-img-max-width, 100%);
    border-radius: 20px;
}

#sections.ytd-guide-renderer > *.ytd-guide-renderer:first-child {
    padding: 12px 0;
    border-radius: 20px;
    background-color: white;
}

#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child) {
    border-bottom: 1px solid rgb(146, 146, 146);
    border-radius: 20px;
    background-color: white;
}

ytd-app:not([background-color-update_]) app-drawer.ytd-app:not([persistent]).ytd-app #header.ytd-app {
    border-bottom: 1px solid rgb(146, 146, 146);
    background-color: white;
}

.text-wrapper.ytd-video-renderer {
    max-width: 600px;
    min-width: 0;
    display: inline-block;
    cursor: pointer;
    text-decoration: none;
    color: var(--yt-endpoint-color, var(--yt-spec-text-primary));
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    background-color: white;
    border-bottom-color: grey;
}

ytd-search[center-results] ytd-two-column-search-results-renderer.ytd-search {
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    -ms-flex: 0 1 100%;
    background-color: #f1f1f1;
}

#content.ytd-rich-item-renderer ytd-post-renderer.ytd-rich-item-renderer, #content.ytd-rich-item-renderer ytd-shared-post-renderer.ytd-rich-item-renderer {
    max-width: 100%;
    width: 100%;
    overflow: hidden;
    border-radius: 20px;
}

#title.ytd-rich-shelf-renderer {
    color: var(--yt-spec-text-primary);
    font-size: 4.5rem;
    font-weight: 500;
    line-height: 14rem;
}

ytd-rich-shelf-renderer {
    border-top: 4px solid rgba(255, 255, 255, 0.1);
    width: 100%;
    overflow-x: hidden;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
}

#items.ytd-watch-next-secondary-results-renderer {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    background-color: white;
}

.ytp-panel-menu {
    padding: 8px 0;
    display: table;
    width: 100%;
    color: #000;
    box-sizing: border-box;
    background-color: #ffffffe0;
    /* border-radius: 20px; */
}

.ytp-popup {
    position: absolute;
    overflow: hidden;
    border-radius: 20px;
    background: rgba(28, 28, 28, 0);
    text-shadow: 0 0 2px rgba(0, 0, 0, 0);
    -moz-transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
    -webkit-transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
    transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
    -moz-user-select: none;
    -ms-user-select: none;
    -webkit-user-select: none;
}

.ytp-menuitem-label-count {
    color: #949494;
}

.ytp-menu-label-secondary {
    font-size: 91%;
    color: #7d7d7d;
}

ytd-menu-popup-renderer {
    display: inline-grid;
    grid-template-columns: min-content;
    background-color: var(--yt-spec-brand-background-primary);
    border-radius: 20px;
    box-shadow: var(--shadow-elevation-2dp_-_box-shadow);
}

#container.ytd-searchbox {
    position: relative;
    align-items: center;
    background-color: #f9f9f9;
    border: 1px solid #f9f9f9;
    border-right: none;
    border-radius: 20px 20 20 2px;
    box-shadow: inset 0 1px 2px #fff0;
    color: var(--yt-searchbox-text-color);
    padding: 2px 6px;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}

#title.ytd-rich-list-header-renderer {
    color: var(--yt-spec-text-primary);
    display: block;
    max-height: 8rem;
    overflow: hidden;
    font-size: 35px;
    font-weight: 20px;
    line-height: 60px;
}

element.style {
    background-color: white;
}

ytd-active-account-header-renderer {
    background-color: var(--yt-spec-brand-background-primary);
    border-bottom: 1px solid var(--yt-spec-10-percent-layer);
    box-sizing: border-box;
    position: relative;
    padding: 16px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    border-radius: 20px;
}

#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child) {
    border-bottom: 1px solid rgba(199, 199, 199, 0);
}

.menu-container.ytd-multi-page-menu-renderer {
    overflow: auto;
    background-color: white;
}

ytd-multi-page-menu-renderer[menu-style=multi-page-menu-style-type-system] {
    width: 300px;
    border-radius: 20px;
}

.content-title.ytd-clarification-renderer {
    padding-bottom: 4px;
    font-size: 30px;
    font-weight: var(--ytd-subheadline-link_-_font-weight);
    line-height: 30px;
}

ytd-clarification-renderer {
    display: block;
    padding: 16px 0 16px 16px;
    background: #ffffff;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    font-size: var(--ytd-user-comment_-_font-size);
    font-weight: var(--ytd-user-comment_-_font-weight);
    line-height: 10px;
    border-radius: 20px;
}

paper-button.ytd-subscribe-button-renderer[subscribed] {
    background-color: rgb(255, 255, 255);
    color: #4c4c4c;
    border-radius: 15px;
}

paper-button.ytd-subscribe-button-renderer {
    background-color: #4197FE;
    border-radius: 15px;
    color: var(--yt-spec-static-brand-white);
    padding: var(--yt-button-padding);
    margin: auto var(--ytd-subscribe-button-margin, 4px);
    white-space: nowrap;
    font-size: var(--ytd-tab-system_-_font-size);
    font-weight: var(--ytd-tab-system_-_font-weight);
    letter-spacing: var(--ytd-tab-system_-_letter-spacing);
    text-transform: var(--ytd-tab-system_-_text-transform);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}

.dropdown-content.paper-menu-button {
    box-shadow: var(--shadow-elevation-2dp_-_box-shadow);
    position: relative;
    border-radius: 20px;
    background-color: var(--paper-menu-button-dropdown-background, var(--primary-background-color));
}

paper-dialog {
    display: block;
    margin: 389px 40px;
    color: var(--paper-dialog-color, var(--primary-text-color));
    font-family: var(--paper-font-body1_-_font-family);
    -webkit-font-smoothing: var(--paper-font-body1_-_-webkit-font-smoothing);
