}

ytd-masthead {
    --paper-input-container-input-align: baseline;
    display: block;
    width: 100%;
    background: rgb(249, 249, 249);
}

#search-icon-legacy.ytd-searchbox {
    cursor: pointer;
    width: 65px;
    border: 1px solid #f9f9f9;
    background-color: #f9f9f9;
    border-radius: 0 2px 2px 0;
    margin: 0;
    --yt-icon-button-icon-width: 24px;
    --yt-icon-button-icon-height: 24px;
}

#contentWrapper.iron-dropdown > * {
    overflow: auto;
    border-radius: 20px;
}

ytd-multi-page-menu-renderer {
    display: inline-block;
    background: rgba(241, 241, 241, 0.98);
    border: 1px solid rgba(0, 0, 0, 0.23);
    border-top: none;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
}

ytd-thumbnail-overlay-side-panel-renderer {
    position: absolute;
    right: 0;
    top: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.32);
    color: var(--yt-spec-static-overlay-text-primary);
    text-align: center;
    font-size: var(--ytd-subheadline_-_font-size);
    font-weight: var(--ytd-subheadline_-_font-weight);
    line-height: var(--ytd-subheadline_-_line-height);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    border-radius: 20px;
}

paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
    background-color: #4197fe;
}

ytd-mini-guide-entry-renderer[active] .guide-icon.ytd-mini-guide-entry-renderer {
    color: #4197fe;
}

ytd-mini-guide-entry-renderer[active] .title.ytd-mini-guide-entry-renderer {
    color: #4197fe;
}

#text.ytd-channel-name {
    display: var(--ytd-channel-name-text-display);
