@-moz-document domain("frontier-rpg.boards.net") {
/* GENERAL */

:root {
  --space-blue: #2788f5;
  --space-haze: rgba(0,0,0,0.6);
}

html {
  font-size: 16px;
}

body {
  font-size: medium;
  font-family: Roboto;
  line-height: 1.5;
}

#wrapper {
   width: 100%;
   max-width: 980px;
 }

a { transition: all 300ms ease;}

a:hover,
a:focus {
  color: orange !important;
}

.container abbr.time { 
  color: inherit;
  font-size: inherit; 
}

hr {
  border-color: rgba(255, 255, 255, 0.25);
}

/* HEADER */

#navigation-menu {
  display: flex;
  justify-content: space-between;
}

#navigation-menu > ul {
  float: none;
}

#navigation-menu > ul li a {
  padding: 0.25rem 0.5rem;
  font-weight: normal;
  font-size: 0.85rem;
  line-height: 1 !important;
}

#navigation-menu > ul li a.state-active,
#navigation-menu > ul li a:hover,
#navigation-menu > ul li a:focus {
  font-weight: normal;
  color: #ffffff !important;
  font-size: 0.85rem;
  line-height: 1 !important;
  border-radius: 3rem;
  background: var(--space-blue);
}

#welcome {
  float: none;
  font-size: 0.85rem;
  line-height: 1 !important;
}

#welcome b {
  font-weight: normal;
}

#welcome a:hover,
#welcome a:focus,
#welcome a:hover font,
#welcome a:focus font {
  color: orange !important;
}

#navigation-menu > b,
#navigation-menu.ui-helper-clearfix:after {
  display: none;
}

.search-filters-button img {
  height: 20px;
}

/* BOARD INDEX */

.rfbdi-board_img_link {
  display: block;
  height: 5rem;
}

.viewing {
  display: block;
  font-family: 'Inconsolata', monospace;
}

.board .board-link {
  font-weight: normal !important;
  font-size: 0.9rem !important;
}

.board .description {						
  line-height: 1.25 !important;
  opacity: 0.75;
}

.moderators {
  display: none;
}

.threads,
.posts,
.latest {
  font-size: 0.85rem;
  font-weight: normal;
}

.latest a {
  font-size: 0.85rem;
}

td.threads,
td.posts {
  font-family: 'Inconsolata', monospace !important;
}

abbr {
  font-weight: normal !important;
}

a.thread-link,
.state-hover a.thread-link {
  font-size: 0.95rem !important;
}



#recent-threads-cell {
  height: auto;
  font-weight: normal;
}

.pad-all {
  padding: 1rem !important;
}

.stats {
  font-weight: normal;
}

.stats .info {
  padding: 1rem;
}

.stats td.icon { 
  display: none;
}

.stats img {
  display: none;
}

.stats th {
  font-weight: normal;
}


/* MINI POFILE */

.even .mini-profile {
  background: #363636;
}

.mini-profile br {
  display: none;
}

.mini-profile .o-user-link {
  display: block;
  margin-bottom: 0.5rem;
  font-size: 1.15rem !important;
}

.mini-profile .o-user-link + img {
  width: 100% !important;
  height: auto !important
}

.mini-profile .custom-title {
  display: none;
}

.mini-profile .avatar {
  position: relative;
  margin: 1rem 0;
