.ui-tabMenu ul li {
  background: #252525;
}

.ui-tabMenu ul li.ui-active {
  background: #3b3b3b;
}

.name_and_group,
.controls {
  font-weight: normal;
}


.show-user td.status-input input {
  width: calc(100% - 1rem);
}

.content-box.center-col,
.content-box.center-col .headings,
.custom-field-rank,
.custom-field-position,
.custom-field-species,
.custom-field-characterage,
.custom-field-charactergender,
.custom-field-discordusername,
.custom-field-playertimezone {
