	background: var(--m-block-d004) !important;
	border: none !important;
	width: 100px !important;
	height: 28px !important;
	line-height: 28px !important;
	border-radius: 14px;
	color: var(--m-light-40);
	transition-property: box-shadow;
	transition-duration: .5s;
	transition-timing-function: ease;
}

.d_badge_bright[class*="d_badge_bawu"] {
	background: var(--m-ecolor) !important;
	color: #fff !important;
}

.d_badge_bright:hover {
	box-shadow: 0 1px 2px 0 rgba(0,0,0,0.08), 0 4px 4px 0 rgba(0,0,0,0.08);
}

.d_badge_bright:after {
	content: "\e866";
	font-family: "Material Icons";
	font-size: 36px;
	line-height: 1;
	position: absolute;
	right: 6px;
	top: -4px;
	color: var(--m-block-d010);
	-moz-transform: translateY(0.125em);
}

.d_badge_title {
	line-height: inherit !important;
	width: 70px !important;
	color: inherit !important;
}

.d_badge_title_bawu {
	text-indent: 8px;
}

.d_badge_bright .d_badge_lv {
	position: absolute;
	top: -2px !important;
	left: auto !important;
	right: 12.5px !important;
	margin: 0 !important;
	height: 100% !important;
	line-height: inherit !important;
	background: none !important;
	color: #fff !important;
	z-index: 1;
	padding: 0;
	text-align: center;
	width: 24px;
	text-indent: 0 !important;
	font-family: inherit !important;
}

.d_author .d_pb_icons .icon_saparater {
	background: none !important;
}

/*【楼层内容域】*/
.d_post_content_main {
	flex: 1;
	width: 0 !important;
	padding: 0 !important;
	background: transparent !important;
	box-sizing: border-box;
}

.p_content {
	padding: 20px !important;
}

.replace_tip {
	text-align: center;
	background-color: rgba(0,0,0,.6) !important;
	border: none !important;
	padding: 0 1px !important;
	padding-top: 7px !important;
	color: #fff;
	text-decoration: none !important;
	opacity: 1 !important;
	transition: background .4s ease;
}

.replace_tip:hover {
	background-color: rgba(0,0,0,.4) !important;
}

.replace_tip:before {
	content: "\e5cf";
	font-family: "Material Icons";
	display: inline-block;
	vertical-align: top;
	font-size: 28px;
	-moz-transform: translateY(0.125em);
}

.replace_tip .txt {
	font-size: inherit;
	padding: 0;
	color: inherit;
}

.replace_tip .expand,
.replace_tip .icon-expand {
	display: none !important;
}

/*禁言tip*/
.forbid-speech-banner {
	border-top: 1px solid var(--m-line-d004) !important;
	width: 100% !important;
	box-sizing: border-box;
	padding: 8px 12px 8px 2px !important;
	margin: 0 !important;
}
.p_forbidden_tip{
	color:  var(--m-light-70) !important;
}

/*【楼层信息域】*/
.core_reply_tail {
	width: 100% !important;
	margin-right: 0 !important;
	position: relative !important;
	margin: 10px 0 !important;
	color: var(--m-light-72) !important;
	float: right;
	box-sizing: border-box;
}

.post-tail-wrap {
	position: static !important;
}

.core_reply_tail > *,
.core_reply_tail > * > * {
	float: left !important;
}

.post-tail-wrap > *,
.p_mtail > * {
	float: right !important;
}

.core_reply_tail > *:not(.p_reply) {
	display: flex;
	background: var(--m-block-d004);
	padding: 0 !important;
	height: 28px;
	line-height: 28px;
	margin: 0 !important;
	font-size: 14px;
}

.core_reply_tail * {
	color: inherit !important;
}

.core_reply_tail a[href]:hover:not(.tail-info),.p_reply:hover {
	color: var(--m-light-60) !important;
}

.post-tail-wrap {
	font-size: 0 !important;
}

.post-tail-wrap > * {
	font-size: 14px !important;
	order: 1;
}

.post-tail-wrap > span.tail-info:last-of-type{
	order: 0;
}

.p_mtail > li {
	font-size: 0 !important;
}

.p_mtail > li >* {
	font-size: 14px !important;
}

.core_reply_tail a {
	color: inherit !important;
}

.core_reply_tail > .props_appraise_wrap {
	padding-top: 5px !important;
	padding-right: 16px !important;
	box-sizing: border-box;
	width: auto;
	min-width: 16px;
	border-radius: 0 16px 16px 0;
}

/*举报按钮*/
.post-tail-wrap>.j_jb_ele,
.j_jb_ele.complaint {
	font-size: 0 !important;
	position: relative;
	width: 20px;
}

.j_jb_ele.complaint .pb_list_triangle_down {
	display: none !important;
}

.post-tail-wrap>.j_jb_ele> a,
.j_jb_ele.complaint > a {
	font-size: 0 !important;
	background: none !important;
}

.lzl_jb {
	display: inline-block !important;
	font-size: 0 !important;
	position: relative;
	width: 22px;
	height: 22px;
	vertical-align: top;
	margin-right: 4px;
	opacity: .6;
	pointer-events: auto;
	transition-property: opacity;
	transition-duration: .4s;
	transition-timing-function: ease;
}

.lzl_jb[style*="none"] {
	opacity: 0;
	pointer-events: none;
}
.post-tail-wrap>.j_jb_ele,
.j_jb_ele.complaint{
	padding: 0;
	background: none !important;
	cursor: pointer;
}
.post-tail-wrap>.j_jb_ele>a,
.j_jb_ele.complaint>a{
	display: none !important;
}
.lzl_jb>.lzl_delate_in:before,
.post-tail-wrap>.j_jb_ele:before,
.lzl_jb_in:before,
.j_jb_ele.complaint:before {
	content: "\e160";
	font-family: "Material Icons";
	line-height: 1;
	display: block;
	font-size: 20px;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
	-moz-transform: translate(-50%, -40%);
	transition-property: color;
	transition-duration: .2s;
	transition-timing-function: ease;
}
.post-tail-wrap>.j_jb_ele:hover,
.j_jb_ele.complaint:hover{
	color: var(--m-light-60) !important;
}

/*删除按钮*/
.lzl_jb>.lzl_delate_in:before,
.lzl_jb_in[data-field*="'delete_mine':'1'"]:before,
.j_jb_ele > a[data-field*="'delete_mine':'1'"]:before {
	content: "\e872";
}

.lzl_jb_in[data-field*="'delete_mine':'1'"]:hover:before,
.j_jb_ele > a[data-field*="'delete_mine':'1'"]:hover:before {
	color: #F44336 !important;
}

.super_jubao {
	display: block !important;
	position: absolute;
	left: 50% !important;
	transform: translateX(-50%);
	background: var(--m-white-color);
	border: 1px solid var(--m-line-b004);
	text-align: center;
	padding: 2px 5px;
	width: 90px;
	top: 28px;
	z-index: 50002;
	font-size: 12px;
	line-height: 28px;
	opacity: 0;
	pointer-events: none;
	transition-property: opacity;
	transition-duration: .5s;
	transition-timing-function: ease;
}

.j_jb_ele:hover .super_jubao {
	opacity: 1;
	pointer-events: auto;
}

.p_post_del_my,
.p_post_del,
.p_post_ban {
	display: inline-block;
	padding: 0 4px;
	text-indent: -1px;
	transition-property: color, text-shadow;
	transition-duration: .4s;
	transition-timing-function: ease;
}

.p_post_del_my:hover,
.core_reply_tail a.p_post_del[href]:hover,
.core_reply_tail a.p_post_ban[href]:hover {
