}

/*回复按钮*/
.p_reply {
	position: absolute;
	top: 0;
	right: 0;
	display: inline-block;
	background: transparent !important;
	margin: 0 !important;
	z-index: 1;
	transition-property: color;
	transition-duration: .2s;
	transition-timing-function: ease;
}

.p_reply > * {
	border: 0 !important;
	display: block !important;
	background: transparent !important;
	color: inherit !important;
	border-radius: 0 !important;
	padding: 0 !important;
	font-size: 0 !important;
	height: 28px !important;
	line-height: 28px !important;
	margin: 0 !important;
	width: 30px;
}

.p_reply > *:before {
	font-family: "Material Icons";
	display: block !important;
	font-size: 20px;
	position: absolute !important;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
	-moz-transform: translate(-50%, -40%);
}

.p_reply .lzl_link_fold {
	padding: 0 !important;
}

.lzl_link_unfold:before {
	content: "\e0bf";
}

.lzl_link_fold:before {
	content: "\e318";
	font-size: 24px;
}

.p_reply[data-field*='"total_num":null'] .lzl_link_unfold:before,
.p_reply[data-field*='"total_num":0'] .lzl_link_unfold:before,
.p_reply[data-field*="'total_num':'0'"] .lzl_link_unfold:before {
	content: "\e0ca";
}

.p_reply > *[style*="none"] {
	display: none !important;
}

.p_reply > *[style*="inline-block"],
.p_reply > *[style*="inline"] {
	display: block !important;
}

