/* i really want this to be global */
textarea,
.btn {
    border-color: #ddd3bb !important;
    border-radius: 6;
}
select .btn {
    cursor: pointer;
}

h1, h2, h3, h4, h5, h6 {
    color: #fff;
}

.hover > a {
    background-color: #4f4f4f!important;
}
.active > a {
    background-color: #e1e1e1!important;
}
.ui-dialog-titlebar-close {
    background-color: #dbdbdb!important;
}

/* BODY */

body {
    background-image: url('https://images.freecreatives.com/wp-content/uploads/2016/02/Light-Parchment-Texture.jpg');
}


@-webkit-keyframes rainbow {
    from {
        -webkit-filter: hue-rotate(10deg);
        filter: hue-rotate(10deg);
    }
    to {
        -webkit-filter: hue-rotate(360deg);
        filter: hue-rotate(360deg);
    }
}


.sheet-rolltemplate-default caption {
    background-image: url(https://images.freecreatives.com/wp-content/uploads/2016/02/Old-Wood-Vector-Background.jpg);
    /*background-color: #237e86!important;*/
    font-weight: 800;
    font-size: 110%;
    -webkit-text-stroke-width: 1px;
    -webkit-text-stroke-color: #000;
     text-shadow: 0px 1px 2px white;
}


/* MENUS */

#sidebarcontrol {

    padding: 6px !important;
    text-align: center !important;
    border: none !important;
    font-size: 12px !important;
    background-color: #e5e2c1 !important;
    top: 10px !important;
    margin-right: 25px;
    transform: none;
    opacity: .8;
}

#sidebarcontrol .fonticon-menu {
    color: #aaa !important;
}


.tabmenu {
    height: 30px !important;
    position: absolute !important;
    left: 0 !important;
    right: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
    top: 0 !important;
    background-color: #e5e2c1!important;
}

.tabmenu li {
    height: 20px !important;
    width: 16.667% !important;
    text-align: center !important;
    top: 0 !important;
    background-color: #e5e2c1!important;
}

.tabmenu li a {
    padding: 0 !important;
    margin: 0 !important;
    line-height: 30px !important;
    border: 0 !important;
    width: 100% !important;
    /*background-image: url(https://images.freecreatives.com/wp-content/uploads/2016/02/Old-Wood-Vector-Background.jpg);*/
    background-color: #e5e2c1!important;
    color: black !important;
    text-shadow: none!important;
}


.ui-slider-handle {
    background-color: #e5e2c1!important;
    background-image: none!important;
}

.ui-tabs-panel {
   background-image: url(https://images.freecreatives.com/wp-content/uploads/2016/02/Light-Parchment-Texture.jpg)!important;
}

.ui-widget-content {
    /*background-color: #e3debd!important;*/
    /*
    background-image: url(https://images.freecreatives.com/wp-content/uploads/2016/02/Light-Parchment-Texture.jpg);
    */
    background-image: url(https://images.freecreatives.com/wp-content/uploads/2016/02/Old-Wood-Vector-Background.jpg);
    background-repeat: repeat;
    /*color: #f00!important;*/
    
}

.ui-dialog {
    border-radius: 0;
    border: none;
}
.ui-dialog-titlebar {
    border-radius: 0;
    border: none;
    border-bottom: 1px solid #424242 !important;
}


.ui-dialog-title {
    color: white!important;
    -webkit-text-stroke-width: .5px;
    -webkit-text-stroke-color: #000;
    text-shadow: 0px 0px 2px gray;
}

.ui-dialog .content {
    color: #fdfad6;
}

.textchatcontainer .rolled {
    border-radius: 0 !important;
    width: 40px !important;
    height: 40px !important;
}

#textchat-input {
    background-image: url(https://images.freecreatives.com/wp-content/uploads/2016/02/Old-Wood-Vector-Background.jpg);
    background-color: #b5afa4 !important;
    border-top: none;
    padding-top: 10px;
}

#textchat-input .ui-autocomplete-input {
    background-color: #e8ddcd !important;
    border: none;
    border-radius: 0;
}

#textchat-input .btn,
#textchat-input .chosen {
    background-image: none;
    background-color: #444 !important;
    border-radius: 0;
    border: none;
    color: #dfdcd1!important;
}
/*
label {
    color: white;
}
*/
#textchat .formula {
    background-color: transparent!important;
    border: none!important;
}

#textchat .message {
    background-color: #d2d2d2;
}

#textchat .message.general.you,
#textchat .message.rollresult.you {
    background-color: #ddd7c9 !important;
}


#textchat .message.desc {
    background-color: #ccc!important;
    color: #111!important;
}
#textchat .message.error {
    background-color: rgba(255, 0, 0, 0.64)!important;
}
#textchat .message.private {
    background-color: rgba(255, 92, 1, 0.33)!important;
}
#textchat .message .spacer {
    background-color: #444;
    margin-top: 10px;
    margin-bottom: 10px;
}
#textchat .message.you .spacer {
    background-color: #005f82;
}

.playerdots {
    background-color: rgba(255, 255, 255, 0)!important;
}

@-moz-document domain("codepen.io") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("codepen.io") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.item-title-area .by {
	text-transform: capitalize!important;
}

.editor-layout-buttons>a:not(:last-child),
.editor-layout-buttons,
:focus {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

body.layout-left .editor-layout-buttons #left-layout svg,
body.layout-right .editor-layout-buttons #right-layout svg,
body.layout-top .editor-layout-buttons #top-layout svg
{
    fill: #ffdd40;
}

.single-item:hover::after, .single-item:focus::after, .single-item:active::after {
    clip-path: none!important;
}



/*___ Fonts ________________________________________________________________________*/

selector {
	font-size: 21px!important;
}

#output pre,
.CodeMirror pre.CodeMirror-line, .CodeMirror pre.CodeMirror-line-like {
	font-size: 19px!important;
}

html {
	font-size: 17px!important;
}

time {
	font-size: 15px!important;
}

selector {
	line-height: 29px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.resource-search-bar .icon-mag {
    margin-left: 0;
}

.single-item::after {
    top: -31px;
    right: -2px;
    bottom: -33px;
    left: -30px;
}

.blog-post {
    margin-top: 18px;
}

/*___ Margins ______________________________________________________________________*/

.main-sidebar {
	margin: 12px!important;
}

.HomepageCommunity_root-O2CAn,
.HomepageCards_root-2dP-p,
.HomepageHeader_root-1kmdV {
	margin: 1rem 3rem!important;
}

/*___ Paddings _____________________________________________________________________*/

.blog-post .post-content,
.blog-post {
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

selector {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

.width-wrapper
{
	max-width: none!important;
}

selector {
	width: auto!important;
}

.width-wrapper
{
	width: 98%!important;
}

.HomepageHeader_root-1kmdV {
    width: 96%!important;
}








}

