}
h6, h6 * {
	font-weight: 500;
	font-size: 1.25rem;
	line-height: 1.6;
	letter-spacing: 0.0075em;
}
p, p *, li, li * {
	font-weight: 300;
	font-size: 1rem;
	line-height: 1.5;
	letter-spacing: 0.00983em;
}
*/
/* header bar background color */
#mw-head,
div.vectorTabs,
div.vectorTabs span,
#searchInput {
	background-image: none;
	background-color: #2e3440;
}

/*section header color*/
#mp-tfp-h2,
#mp-tfa-h2,
.warningbox,
.mw-body h2,
.k-player .control-bar,
table.tmbox-protection,
table.wikitable > caption,
.suggestions-result-current,
.wikiEditor-ui-toolbar .booklet .index .current {
	/*
    background: #4d807f !important;
    border: 1px solid var(--background-medium) !important;
    */
	background: var(--background-light) !important;
	border: none;
	border-radius: 3px;
	box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

.toctitle {
	/*
    background: #4d807f !important;
    border: 1px solid var(--background-medium) !important;
    */
	background: var(--background-light) !important;
	border: none;
	border-radius: 3px;
	box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

.toctitle h2#mw-toc-heading {
	color: #fff !important;
}

.toctogglespan {
	display: none;
}

/* body color */
body,
code,
table,
.mw-body,
#mp-left,
.quotebox,
.catlinks,
#mp-right,
#mp-bottom,
div#footer,
#wpTextbox1,
.MainPageBG,
.boilerplate,
.footer-sidebar,
div#simpleSearch,
#searchInput,
.lang-list-button,
.lang-list-content,
.mw-highlight pre,
table.ombox-notice,
.vertical-navbox th,
.lang-list-button-wrapper,
.bookshelf-container .text,
.shortcutbox.plainlist.noprint,
body.page-Main_Page #mp-topbanner,
table.toccolours.collapsible.collapsed,
.lang-list-active .lang-list-container,
.no-js .lang-list-container {
	color: #fff;
	background: var(--background-dark) !important;
	border: 0px !important;
}
.wikiEditor-ui-toolbar .page-characters div span:hover {
	background: var(--background-dark) !important;
	border: 1px solid #ddd !important;
}

/* card color */
#toc,
tbody,
thead,
select,
.navbox,
.barbox,
#wpSave,
#filetoc,
.infobox,
.navbox th,
.barbox th,
.barbox tr,
.barbox td,
.navbox th,
.wikitable,
tbody table,
.navbox-list,
.navbox-title,
div.thumbinner,
td.diff-context,
.vertical-navbox,
td.diff-addedline,
table.tmbox-notice,
table.fmbox-system,
input[type="file"],
.mw-mmv-post-image,
.NavFrame.collapsed,
td.diff-deletedline,
#mw-history-compare,
table.ambox-content,
table.toccolours,
input[type="reset"],
input[type="submit"],
input[type="button"],
.suggestions-special,
.infobox div.NavHead,
.suggestions-results,
.mw-mmv-image-metadata,
.wikiEditor-ui-toolbar,
#mw-history-compare li,
#typeahead-suggestions,
.oo-ui-popupWidget-popup,
table.tmbox-notice table,
#pagehistory li.selected,
div#editpage-specialchars,
div#editpage-specialchars a,
.fancycaptcha-image-container,
.fancycaptcha-captcha-container,
html body.mediawiki .mbox-small,
.noprint.portal.plainlist.tright,
.infobox.standard-talk.bordered th,
.mw-editform-legacy input#wpSummary,
.jq-fullscreened .mw-mmv-post-image,
.mw-mmv-post-image.mw-mmv-untruncated,
.oo-ui-popupToolGroup .oo-ui-tool-link,
.wikiEditor-ui-toolbar .booklet .pages,
.wikiEditor-ui-toolbar .sections .section,
.wikiEditor-ui-toolbar .group .tool-select,
.wikiEditor-ui-toolbar .group .tool-select .label,
.wikiEditor-ui-toolbar .group .tool-select .menu .options,
.wikiEditor-ui-toolbar .group .tool-select .options .option,
.ve-init-mw-desktopArticleTarget-toolbar-opened > .oo-ui-toolbar-bar,
.vertical-navbox.nowraplinks.plainlist td .NavFrame.collapsed .NavHead,
.oo-ui-popupToolGroup.oo-ui-widget-enabled > .oo-ui-popupToolGroup-handle {
	background: var(--background-medium) !important;
	border: 3px solid var(--background-medium) !important;
}
.wikiEditor-ui .wikiEditor-ui-view,
.oo-ui-popupToolGroup.oo-ui-widget-enabled.oo-ui-popupToolGroup-active {
	border: 0px !important;
}
/* link colors */
a,
#toc h2,
li.selected a:visited,
div.vectorTabs li a:visited,
span.mw-editsection-bracket {
	color: #8fbcbb !important;
}
a:hover {
	color: #b48ead !important;
	text-decoration: none;
}
a:visited {
	color: #d08770 !important;
}
a.external.text {
	color: #a3be8c !important;
}
a.new {
	color: #bf616a;
}
.mw-body-content a.extiw {
	color: #81a1c1;
}
.plainlist ol,
.plainlist ul {
	background: inherit !important;
}

/* Table background colors */
td.table-yes {
	background: #40bf9f !important;
}
td.table-no {
	background: #bf616a !important;
}
td.unknown.table-unknown,
#wpTextbox1,
.sitedir-ltr input {
	background: #4c566a !important;
}
td.table-partial {
	background: #499fb6 !important;
}

/* table border color */
td,
tr,
th {
	border-color: var(--element-gray) !important;
}

/*current page underline color*/
div.vectorTabs li.selected {
	border-bottom: 2px solid #fff;
}

/* misc */
fieldset {
	border-color: #8fbcbb;
}
.navbox-list ul {
	margin: 10px 0px;
	border-top: 0px;
}
table.wikitable {
	border: 1px solid #fff !important;
}
.mwe-math-fallback-image-inline {
	background: #8fbcbb;
	border: 3px solid #8fbcbb !important;
	border-radius: 3px;
}

/* End colors */
/* section header styling */
#mp-tfp-h2,
#mp-tfa-h2,
.warningbox,
.mw-body h2,
table.tmbox-notice,
table.tmbox-protection {
	margin-top: 50px;
	margin-bottom: 20px;
	padding-left: 10px;
	border-radius: 3px;
	box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

/* card styling */
#toc,
.navbox,
.barbox,
.infobox,
.wikitable,
div.thumbinner,
.vertical-navbox,
table.cquote,
table.toccolours,
table.ambox-notice,
table.fmbox-system,
table.ambox-content,
#mw-history-compare,
html body.mediawiki .mbox-small,
.fancycaptcha-captcha-container,
.noprint.portal.plainlist.tright,
.ve-init-mw-editSwitch .oo-ui-popupToolGroup .oo-ui-toolGroup-tools {
	border-radius: 3px;
	box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
	margin: 30px !important;
}

/*article title*/
.mw-body .firstHeading {
	position: relative;
	top: 8px;
	z-index: 1;
	border: 0px;
	max-height: 51px;
	overflow: hidden;
	font-weight: 100;
	font-size: 35px;
	background-color: var(--background-light);
	width: calc(100% - 10px);
	padding-left: 10px;
	border-radius: 3px;
	box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

/*article title; special rules for italics*/
.mw-body .firstHeading i {
	position: relative;
	top: -7px;
	z-index: 1;
	width: 68%;
	border: 0px;
	max-height: 51px;
	overflow: auto;
	font-weight: 100;
	font-size: 35px;
}

/*Header bar styling*/
#mw-head {
	height: 87px;
	box-shadow: 0px 0px 10px 0px #2e3440;
}

/*hide side panel*/
#mw-panel {
	display: none;
}

/*paragraph styling*/
p {
	display: block;
	width: 100%;
	margin: auto;
}
.mw-body {
	margin: 0px 30px;
	border: 0px;
}

/*header bar profile links*/
#p-personal li,
#p-personal a {
	line-height: 1;
	margin: 0px 2px;
	font-size: 12px;
}

/*header navigation links*/
#left-navigation {
	position: absolute;
	margin-left: 1em;
	margin-top: 2.5em;
	float: left;
}
#right-navigation a,
#left-navigation a {
	margin: 0px 20px;
}

/*search bar styling*/
div#simpleSearch {
	bottom: 15px;
	height: 40px;
	border: 0px;
}
div#simpleSearch input {
	position: absolute;
	right: -7px;
	width: 100%;
	top: 8px;
}

/*Info card title styling*/
.infobox caption {
	border: 3px;
	border-color: inherit;
	border-top-left-radius: 3px;
	border-top-right-radius: 3px;
	background: inherit;
	font-weight: 100;
	padding: 10px;
}

/*card text styling*/
.infobox td,
.infobox th,
.infobox tr,
.infobox div.NavFrame div.NavHead,
.vertical-navbox.nowraplinks.vcard.hlist div.NavFrame div.NavHead {
	background: inherit !important;
}
.infobox div.NavFrame div.NavHead,
.vertical-navbox.nowraplinks.vcard.hlist div.NavFrame div.NavHead {
	border: 0px !important;
}
/*Table of contents header*/
#toc h2,
#toctitle.toctitle,
.infobox table.wikitable {
	font-weight: 400;
	background: inherit !important;
	box-shadow: none;
}

/*
#contentSub,
#contentSub2 {
	margin: 0 0 5.4em 1em; 
}
*/

/*section header font size*/
.mw-headline,
.mw-headline i,
.mw-body h2 a,
.mw-body h2 i {
	font-size: 25px !important;
}

/* Editor styling*/
.editOptions {
	border: 0px;
	background: inherit;
}
.wikiEditor-ui .wikiEditor-ui-top {
	border: 0px;
}
.wikiEditor-ui-toolbar,
div.wikiEditor-ui-clear + div#editpage-specialchars {
	box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

.oo-ui-popupToolGroup.oo-ui-widget-enabled > .oo-ui-popupToolGroup-handle {
	position: absolute;
	top: -3px !important;
	height: 67%;
}
.ve-init-mw-editSwitch .oo-ui-popupToolGroup .oo-ui-toolGroup-tools {
	border: 0px !important;
}

/* media player styling */
.k-player .k-options {
	border: 0px !important;
	margin-top: -4px;
}
.k-player .k-options span {
	position: relative;
	top: 0px;
}

/*tweaks*/
.noprint.navbox.metadata.plainlist,
.vertical-navbox.nowraplinks.infobox,
.infobox.sisterproject.plainlinks.haudio,
.navbox + .navbox,
table.metadata.mbox-small,
.nocolbreak,
div.columns li,
div.columns dd dd,
p {
	margin-top: 20px !important;
}
.noprint.Inline-Template.Template-Fact span,
sup.reference a {
	font-size: 12px;
}
pre,
code,
.mw-code {
	background: inherit !important;
	font-family: monospace !important;
}

td,
tr,
