	.table_report_webvisor .table__body-row_viewed_yes .table__td {
		opacity: 1;
		color: white;
		background-color: dimgray;
	}
	.table_report_webvisor .table__body-row:hover .table__td,
	.table_report_webvisor .table__body-row_viewed_yes:hover .table__td:first-child {
		background-color: khaki;
		color: black;
	}
	.toggle-expand_theme_light .icon__minus,
	.toggle-expand_theme_light .icon__plus,
	.visit-favorite__flag-icon {
		opacity: 1;
	}
	.icon__plus,
	.icon__minus,
	.icon__flag {
		background-image: none;
	}
	.icon__plus:after,
	.icon__minus:after,
	.icon__flag:after {
		visibility: visible;
		opacity: 1;
	}
	.icon__plus:after {
		content: '\002B';
		font-size: 14pt;
	}
	.icon__minus:after {
		content: '\2212';
		font-size: 14pt;
	}
	.icon__flag:after {
		content: "\1F3C1";
	}
	.table_report_webvisor .table__body-row_viewed_yes .table__td .link_external_yes,
	.humanize-attributes__dimension-icon {
