/* ==UserStyle==
@name			Youtube Music Redesign
@author			fakecore
@version		0.4
@namespace		ytmredesign2020
==/UserStyle== */

@-moz-document domain("music.youtube.com")
{
	ytmusic-player[player-ui-state_="MINIPLAYER"] {
		display: none !important;
	}
	
	#nav-bar-background,
	#nav-bar-shadow {
		display: none !important;
	}
	
	#left-content {
		margin-top: 14px;
		margin-left: 16px;
	}
	
	#left-content:after {
		position: absolute;
		content: 'Music';
		left: 55px;
		top: 10px;
		font-size: var(--ytmusic-display-2_-_font-size);
		font-weight: 700;
		font-family: YT Sans, Roboto, Noto Naskh Arabic UI, Arial, sans-serif;
		color: #000;
	
	}
	
	#right-content {
		justify-content: right;
	}
	
	ytmusic-item-section-tabbed-header-renderer {
		flex-flow: row;
	}
	
	ytmusic-nav-bar {
		height: 100vh !important;
		padding: unset;
		width: 256px !important;
		align-items: flex-start;
		border-right: 1px solid #ddd;
		background-color: #fff;
		margin-top: 0;
		z-index: 100000;
	}
	
	ytmusic-pivot-bar-item-renderer,
	ytmusic-pivot-bar-item-renderer:hover, ytmusic-pivot-bar-item-renderer.iron-selected {
		color: #333;
	}
	
	.ytmusic-nav-bar {
		flex-flow: column;
	}
	
	.center-content.ytmusic-nav-bar {
		height: 320px;
		flex-flow: column-reverse;
		justify-content: flex-end;
		padding: 54px 0;
	}
	
	ytmusic-pivot-bar-item-renderer {
		font-size: 14px;
		margin: 0 !important;
		padding: 8px 16px !important;
	}
	
	input.ytmusic-search-box, #placeholder.ytmusic-search-box {
		font-size: 14px;
	}
	
	.ytmusic-search-box paper-icon-button {
		width: 32px;
		height: 32px;
	}
	
	.search-container.ytmusic-search-box, ytmusic-search-box {
		border-radius: unset !important;
	}
	
	.search-box.ytmusic-search-box {
		background-color: #fafafa !important;
		color: #aaa;
		margin: 0 0 16px;
		height: 42px;
		justify-content: initial;
	}
	.search-box.ytmusic-search-box:hover{
		background-color: #f5f5f5 !important;
	}
	#placeholder.ytmusic-search-box{
		padding: 8px 128px 8px 0;
	}
	
	ytmusic-search-box[has-query] input.ytmusic-search-box, ytmusic-search-box[opened] input.ytmusic-search-box {
		color: #333 !important;
	}
	
	ytmusic-search-box[opened] .ytmusic-search-box .search-box,
	ytmusic-search-box[has-query] .ytmusic-search-box .search-box {
		margin: 0 !important;
		background-color: #fafafa !important;
	}
	
	ytmusic-search-box[has-query] .search-container.ytmusic-search-box, ytmusic-search-box[opened] .search-container.ytmusic-search-box {
		box-shadow: unset;
	}
	
	ytmusic-search-box {
		width: 255px !important;
	
