	background: url(https://i.ibb.co/GdCDBGN/present-gift.png) no-repeat!important;
	box-sizing: border-box;
}

.gifts-menu svg {
    background-size: 24px!important;
    height: 26px!important;
    padding-left: 26px!important;
    width: 10px!important;
}


/* List Button ____________________________________________________________________*/

.svg-icon.svg-icon-nfplayerEpisodes {
	background: url(https://i.ibb.co/t3DSL5S/list.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 54px!important;
	height: 54px!important;
	padding-left: 54px!important;
	background-size: 54px!important;

}

/* next Button ____________________________________________________________________*/

.svg-icon.svg-icon-nfplayerNextEpisode {
	background: url(https://i.ibb.co/DVFmn9r/next.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 54px!important;
	height: 54px!important;
	padding-left: 54px!important;
    background-size: 54px!important;
}

/* Notification ____________________________________________________________________*/

.icon-button-notification:before {
	content: '';
}

.icon-button-notification:before {
	background: url(https://i.ibb.co/8Px680k/Notification.png) no-repeat!important;
	box-sizing: border-box!important;
}

.icon-button-notification:before {
	padding-left: 32px!important;
    background-size: 31px!important;
}

/* Pause Button ____________________________________________________________________*/

.svg-icon.svg-icon-nfplayerPause {
	background: url(https://i.ibb.co/dm3ydLj/pause.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 54px!important;
	height: 54px!important;
	padding-left: 54px!important;
    background-size: 54px!important;
}

/* Play Button ____________________________________________________________________*/

.svg-icon.svg-icon-play-with-ring,
.svg-icon.svg-icon-nfplayerPlay {
	background: url(https://i.ibb.co/fHQFmYd/play.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 54px!important;
	height: 54px!important;
	padding-left: 49px!important;
    background-size: 54px!important;
}

/* Question ____________________________________________________________________*/

.svg-icon.svg-icon-nfplayerReportAProblem {
	background: url(https://i.ibb.co/1btLzWv/question.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 54px!important;
	height: 54px!important;
	padding-left: 54px!important;
    background-size: 54px!important;
}

/* Search ____________________________________________________________________________*/

[aria-label="Search"] span:before {
	content: ""!important;
}

[aria-label="Search"] span:before {
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
	box-sizing: border-box!important;
}

[aria-label="Search"] span:before {
    height: 24px!important;
    padding-left: 24px!important;
    width: 24px!important;
    background-size: 18px!important;
}

/* Settings Button ____________________________________________________________________*/

.svg-icon.svg-icon-nfplayerSubtitles, .svg-icon.svg-icon-nfplayerSubtitles {
    background: url(https://i.ibb.co/YtNHHpK/Setting-icon-32x32.png) no-repeat!important;
    box-sizing: border-box!important;
    width: 54px!important;
    height: 54px!important;
    padding-left: 54px!important;
    background-size: 54px!important;
}

/* Thumbs Up/Down __________________________________________________________________________*/

.meta-thumb-container.thumb-up svg,
[aria-label="Already rated: thumbs up (click to remove rating)"] svg,
[aria-label="Rate thumbs up"] svg {
	background: url(https://i.ibb.co/MpqLxDN/Thumbs-Up-Skin-Color.png) no-repeat!important;
	box-sizing: border-box!important;
}

.meta-thumb-container.thumb-up svg,
[aria-label="Already rated: thumbs up (click to remove rating)"] svg,
[aria-label="Rate thumbs up"] svg {
	background-size: 22px!important;
	height: 24px!important;
	padding-left: 24px!important;
	width: 24px!important;
}

[aria-label="Rate thumbs down"] svg {
	background: url(https://i.ibb.co/6XfG8VP/thumbs-dowm.png) no-repeat!important;
	box-sizing: border-box!important;
}

[aria-label="Rate thumbs down"] svg {
	background-size: 22px!important;
	height: 24px!important;
	padding-left: 24px!important;
	width: 24px!important;
}

/* Volumn Button ____________________________________________________________________*/

[aria-label="Turn audio on"] svg {
	background: url(https://i.ibb.co/nc4MCW5/volumn-on.png) no-repeat!important;
	box-sizing: border-box!important;
}

[aria-label="Turn audio on"] .svg-icon.svg-icon-audio-off {
	width: 42px!important;
	height: 36px!important;
	padding-left: 42px!important;
}

/* Volumn Button ____________________________________________________________________*/

[aria-label="Volume"] svg,
.svg-icon.svg-icon-volumeLow,
.svg-icon.svg-icon-volumeMax {
	background: url(https://i.ibb.co/nc4MCW5/volumn-on.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 54px!important;
	height: 54px!important;
	padding-left: 54px!important;
    background-size: 54px!important;
}

[aria-label="Muted"] svg {
	background: url(https://i.ibb.co/ykbZZg7/volumn-off.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 54px!important;
	height: 54px!important;
	padding-left: 54px!important;
    background-size: 54px!important;
}

}

