@-moz-document url("https://www.wcostream.com/") {
/* Ads & Annoyances */
html body > div:not(#wrap) {display:none !important}
html *[style*="clear:both"] {display: none !important}

/* Main Background */
html body {background: rgb(30,34,36)}

/* Nav */
html body #wrap{width: 100%;margin: 0;}
#wrap .topb{background: rgb(30, 34, 36);position: fixed;width: 100%;height: 60px;border-bottom: 2px inset rgb(200, 80, 0);box-shadow: 0px 2px 8px rgb(10, 14, 16);z-index: 2;}
#wrap .topb > tbody > tr > td:nth-of-type(1){display: none;}
#wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody{display: flex;flex-direction: row;flex-wrap: nowrap;}
#wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody > tr:nth-of-type(1){display: none;}
#wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody > tr:nth-of-type(2){flex: 1 1 auto;}
#wrap #navbar{margin: 0;}
#wrap #navbar > #nav{display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;}
#wrap #navbar > #nav > li > a{height: 40px;width: auto;line-height: 40px;margin: 10px 4px;padding:0 10px;text-align: center;color: rgb(220, 220, 220);border-radius: 4px;transition: .2s linear}
#wrap #navbar > #nav > li > a:hover{background: rgb(250, 120, 0);color: white;}
#wrap .bh{display: none;}
#wrap .animesearch{background: rgb(30, 34, 36);padding: 0;margin: 0;height: 60px;}
#wrap .animesearch > form#konuara{display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;padding: 10px 0;}
#wrap .animesearch > form#konuara > input#searchbox{background: rgb(20, 24, 26);height: 40px;margin: 0;padding: 0 10px 0 10px;float: unset;border: none;color: rgb(220, 220, 220);}
#wrap .animesearch > form#konuara > .searchboxtop{margin: 0;}
#wrap .animesearch > form#konuara > .searchboxtop > select{padding: 0;height: 40px;width: 140px;text-align: center;border: none;background: rgb(40, 44, 46);box-shadow: none;color: rgb(220, 220, 220);font-weight: 600;}
#wrap .animesearch > form#konuara > .searchboxtop > select:hover{background: rgb(50, 54, 56);}


/* Content Main Page */
html body #wrap #content{padding: 80px 0 0 0;background: rgb(30,34,36);width: 100%;margin: 0;display: grid;grid-template-columns: auto 150px 250px;grid-template-rows: 400px auto 40px;}
html body #wrap #content > .alert{display: none}


/* RecentReleases */
#wrap #content > table{width: calc(100% - 20px);grid-area: 1/1/2/4;}
#wrap #content > table > tbody > tr > td:nth-of-type(1){background: transparent !important;height: 400px !important;}
#wrap #content > table > tbody > tr > td:nth-of-type(1) > table{width: unset;margin-right: 10px;height: 400px;}
#wrap .manset{background: transparent;float: unset;margin: 0;padding: 0;display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;}
#wrap .manset > .shadetabs{padding: 0;margin: 0;text-align: center;height: 400px;width: 120px;flex: 0 0 auto;display: flex;flex-direction: column;flex-wrap: nowrap;background: rgb(60, 64, 66);box-shadow: 2px 0 4px rgb(30, 34, 36);z-index: 1;border-radius: 4px 0 0 4px;}
#wrap .manset > .shadetabs > li{background: transparent;border: transparent;display: block;margin: 20px 0;padding: 0;height: 40px;width: 120px;flex: 0 0 auto;}
#wrap .manset > .shadetabs > li > a{height: 40px;width: 120px;padding: 0;display: block;color: rgb(220, 220, 220);line-height: 40px;background: rgb(60, 64, 66);border-radius: 0;transition: .2s;}
#wrap .manset > .shadetabs > li > a:hover{background: rgb(250, 120, 0);border-radius: 4px;color: white;box-shadow: 0 2px 4px rgb(20, 24, 26);}
#wrap .tabcontent > .mansetlisteleme{float: unset;height: 400px;}
#wrap .tabcontent > .mansetlisteleme > ul{float: unset;display: flex;flex-direction: row;flex-wrap: wrap;flex: 0 0 auto;padding: 10px 0;background: rgb(40, 44, 46);margin: 0;height: 380px;width: unset;overflow: hidden;border-radius: 0 4px 4px 0;}
#wrap .tabcontent > .mansetlisteleme > ul > li{background: transparent;float: unset;height: 50px;width: 48%;margin: 0 auto;}
#wrap .tabcontent > .mansetlisteleme > ul > li > a{background: rgb(80, 84, 86);color: rgb(220, 220, 220);text-shadow: none;float: unset;padding: 10px;display: block;border-radius: 4px;transition: .2s;height: 20px;}
#wrap .tabcontent > .mansetlisteleme > ul > li > a:hover{background: rgb(250, 120, 0);color: white;box-shadow: 0 4px 4px rgb(20, 24, 26);}
#wrap #content > table > tbody > tr > td:nth-of-type(2){display: none;}


/* Today's Anime */
#content > table > tbody > tr > td:nth-of-type(3) {width: 400px;}
