#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(1) {display:none}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table {height:320px;width:400px;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(1) > td {height:40px;width:400px}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(1) > td > .last {height:40px;width:400px;margin: 0;display: block;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(1) > td > .last a {height:40px;width:400px;display: block;margin: 0;text-align: center;line-height: 40px;font-weight: 600;font-size: 16px;box-shadow: 0 2px 4px rgb(20, 24, 26);background:rgb(60, 64, 66);border-radius:4px 4px 0 0}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(2) > td {display:flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;height: 220px;background: rgb(40, 44, 46);padding: 10px 0;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(2) > td > .catimg {height: 200px;width: 150px;margin: 10px;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(2) > td > .catimg > img {border: none;border-radius:4px}


/* Content Header bar */
html body > div#wrap > div#content > div:nth-of-type(2) {display:none}
html body > div#wrap > div#content > div:nth-of-type(3) {max-width: unset !important;float: unset !important;margin: 0 10px 0 10px !important;grid-area: 2/1/3/3;}
html  body > div#wrap > div#content > div:nth-of-type(3) > div:nth-of-type(1) {display:none}
#wrap .rr_header_bar {margin-top: 10px;}
#wrap .rr_header_bar > ul {background: rgb(60, 64, 66);border-radius: 4px;margin: 0;display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;justify-content: space-evenly;}
#wrap .rr_header_bar > ul > li {height: 40px;width: 120px;margin: 0 10px;}
#wrap .rr_header_bar > ul > li > a {background: rgb(60, 64, 66);padding: 0 10px;height: 40px;width: 120px;text-align: center;line-height: 40px;border-radius:4px;transition: .2s;color: rgb(220, 220, 220);}
#wrap .rr_header_bar > ul > li > a:hover {background:rgb(250, 120, 0);color:white}
#wrap .recent-release {margin-top: 20px;height: 60px;border-radius: 4px 4px 0 0;color: rgb(220, 220, 220);font-size: 20px;line-height: 60px;padding: 0 20px;font-weight: 600;background: rgb(40, 44, 46);text-align: center}
#wrap .sidebar_body {background:rgb(60, 64, 66);border:none;max-width: unset;border-radius: 0;padding: 0}
#wrap .sidebar_body > .items {margin:0 20px;padding: 0;display: flex;flex-direction: row;flex-wrap: wrap;flex: 0 0 auto;justify-content: space-around}
#wrap .sidebar_body > .items > li {width: 150px;height: 260px;display: flex;flex-direction: column;flex: 0 0 auto;flex-wrap: nowrap;justify-content: flex-start;margin: 10px 20px;padding: 10px 20px;background: rgb(30, 34, 36);border-radius: 4px;transition:.2s linear}
#wrap .sidebar_body > .items > li:hover {background:rgb(250, 120, 0)}
#wrap .sidebar_body > .items > li:hover > .recent-release-episodes > a {color:white}
