}
.popupContent {
    background: #000;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #f00;
}
.gwt-TabBar .gwt-TabBarItem:hover,
.gwt-TabBar .gwt-TabBarItem:focus {
    color: #f00;
}

/* Smooth Cars & Gray Progress Bar*/
.scoreboard .row .progressBar {
    border-color: #292723;
    border-bottom-style: solid;
    border-width: 4px;
    transition: linear 2s;
}
}

@-moz-document url-prefix("https://studio.youtube.com/channel/") {
#dialog.style-scope.ytcp-uploads-dialog {
    max-height: 100vh !important;
    width: 100vw !important;
    left: 0 !important;
}

paper-dialog.ytcp-uploads-dialog {
    margin: 0 !important;
    border-radius: 0 !important;
}

#details > div,
#dialog > div > div.header.style-scope.ytcp-uploads-dialog > ytcp-animatable > ytcp-stepper > div {
    margin-left: 12% !important;
}

@media only screen and (max-width: 900px) {
    #details > div,
    #dialog > div > div.header.style-scope.ytcp-uploads-dialog > ytcp-animatable > ytcp-stepper > div {
        margin-left: 0 !important;
    }
}
}
