}
.u_cbox .u_cbox_list .u_cbox_text_wrap {
	color: var(--dark-theme-text-color);
}
.u_cbox .u_cbox_list .u_cbox_ico_level, .u_cbox .u_cbox_list .u_cbox_in_report, .u_cbox .u_cbox_list .u_cbox_date {
