@-moz-document domain("lazyfoo.net") {
/* latin-ext */
@font-face {
    font-family: 'Ubuntu Mono';
    font-style: normal;
    font-weight: 400;
    src: local('Ubuntu Mono'), local('UbuntuMono-Regular'), url(https://fonts.gstatic.com/s/ubuntumono/v9/KFOjCneDtsqEr0keqCMhbCc0CsTKlA.woff2) format('woff2');
    unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}

/* latin */
@font-face {
    font-family: 'Ubuntu Mono';
    font-style: normal;
    font-weight: 400;
    src: local('Ubuntu Mono'), local('UbuntuMono-Regular'), url(https://fonts.gstatic.com/s/ubuntumono/v9/KFOjCneDtsqEr0keqCMhbCc6CsQ.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

:root {
    --background-color: hsl(0, 7%, 97%);
    --background-color-lighter: hsl(50, 2%, 99%);
    --background-color-darker: hsl(50, 5%, 94%);
    --main-text-color: #1e1e24;
    --main-text-color-lighter: #7f7f94;
    --code-text-color: hsl(207, 68%, 15%);
    --header-text-color: #003459;
    --primary-color: #2F97C1;
    --secondary-color: #FA7E61;
    --border-color: #d8d8d8;
}

body {
    font-family: 'Trebuchet MS', 'Roboto', Sans-Serif;
    background: var(--background-color);
    color: var(--main-text-color);
}

/* * * * * * * * * *
Text
* * * * * * * * * */
/* Site name */
body > div > h1 {
    font-size: 15px;
}

a {
    color: var(--primary-color) !important;
}

/* Header links */
.nav a {
    font-size: 14px;
}

/* Title */
h1, h2, h3 {
    color: var(--header-text-color);
}

/* He uses them for "Last Updated" messages */
h6 {
    margin: 5px 0 15px 0;
    color: var(--main-text-color-lighter);
    font-weight: lighter;
}

/* Copyright */
.footer h6 {
    color: var(--main-text-color);
}

/* On some pages he makes <b> text a different color */
b {
    color: var(--main-text-color);
}

/* * * * * * * * * *
Divs
* * * * * * * * * */
.tutPreface {
    margin: 0 auto auto auto !important;
    padding: 0 !important;
}

.tutImg {
    margin: 25px auto 0 auto !important;
}

/* Includes tables */
.tutCode, .tutPreface, .tutText, .tutFooter, .tutToC, .artToC {
    margin: 20px auto !important;
    width: 75%;
    background: none !important;
    border: none !important;
}

.tutCode {
    /* Spacing */
    padding: 20px;
    /* Font */
    font-family: 'Ubuntu Mono', 'Roboto Mono', 'Lucida Console', monospace;
    color: var(--code-text-color) !important;
    white-space: pre-wrap !important;
    /* Other */
    background: var(--background-color-darker) !important;
    border-radius: 10px;
    border-left: solid var(--secondary-color) 3px !important;
}

.tutText {
    line-height: 1.5 !important;
}

/* * * * * * * * * * *
Tables
* * * * * * * * * * */
table {
    border-collapse: collapse;
    border-radius: 10px;
    -webkit-box-shadow: 0 0 10px 2px hsl(0, 0%, 80%);
    box-shadow: 0 0 10px 2px hsl(0, 0%, 80%);
}

/* Table shadow */
tr:first-child td:first-child {
    border-radius: 10px 0 0 0;
}

tr:first-child td:last-child {
    border-radius: 0 10px 0 0;
}

tr:first-child td:only-child {
    border-radius: 10px 10px 0 0;
}

tr:last-child td:first-child {
    border-radius: 0 0 0 10px;
}

tr:last-child td:last-child {
    border-radius: 0 0 10px 0;
}

/* Title row */
tr:first-child {
    padding: 13px !important;
    background: var(--primary-color) !important;
    font-size: 1.5em;
    font-weight: bold;
    text-align: center !important;
}

tr {
    background: var(--background-color-lighter) !important;
}

tr:not(:first-child) {
    border-top: solid 1px var(--border-color);
}

td {
    border: none !important;
    background: none !important;
    padding: 11px 25px;
}

/* * * * * * * * * *
Other
* * * * * * * * * */
.tutPreface .tutImg img {
    /* Dims */
    height: 100px;
    width: auto;
    /* Border */
    border-color: var(--secondary-color) var(--primary-color);
    border-radius: 15px;
    border-style: solid;
    border-width: 6px;
}

/* * * * * * * * * *
Media Queries
* * * * * * * * * */
@media only screen and (max-width: 880px) {
    body {
        font-size: 15px;
    }

    .tutCode, .tutPreface, .tutText, .tutFooter, .tutToC, .artToC {
        width: 90% !important;
    }
}

@media only screen and (min-width: 1200px) {
    body {
        font-size: 18px;
    }

    .tutCode, .tutPreface, .tutText, .tutFooter, .tutToC, .artToC {
        width: 850px !important;
    }
}
}

