}

.tutToC col:first-child {
    width: 40%;
}

.tutToC col:last-child {
    width: 60%;
}

.ToCTitle {
    font-size: 1em;
}

.tutToC td {
    padding: 7px;
}
}

@-moz-document url-prefix("https://lazyfoo.net/contact.php") {
/* * * * * * * * * * *
Google Search Box 
* * * * * * * * * * */
form {
    margin-top: 25px;
}

font {
    color: var(--main-text-color);
}

a[href="http://www.google.com/"] img {
    content: url("https://upload.wikimedia.org/wikipedia/commons/thumb/2/2f/Google_2015_logo.svg/1200px-Google_2015_logo.svg.png");
    width: 100px;
    padding-top: 5px;
}

input {
    padding: 8px;
    border-radius: 3px;
    outline: none;
    transition: all 0.3s;
    font-family: 'Trebuchet MS', Sans-Serif;
}

#sbi {
    color: var(--main-text-color);
    outline: none !important;
    background: var(--background-color-darker);
    border: 1px solid var(--border-color) !important;
}

#sbi:focus {
    border-color: var(--secondary-text-color) !important;
}

#sbi:hover:not(:focus) {
    border-color: darkgray !important;
}

#sbb {
    border: none;
    background: var(--primary-color);
    box-shadow: 0 2px 10px -5px #000;
}

#sbb:hover {
    background: #b072de;
    cursor: pointer;
}

/* Remove shadows and colors in nested tables */
td table {
    -webkit-box-shadow: none;
    box-shadow: none;
}

td td {
    padding: 0;
    background: var(--background-color-darker) !important;
}
}

