    --bshadow: 0 2px 4px var(--shadow);
    --app-width: 1396px;
    --emoji-o: 0.8;
    --avatar-r: 50%;
    --bg-image: url("https://images.unsplash.com/photo-1434569117012-ce134ee1a088?fit=crop&w=1280&h=720&q=80");
    --bg-opacity: 0.4;
    --bg-blur: 12px;
    --bg-hue: 320deg;
    --bg-invert: 0;
