@-moz-document domain("app.mozenda.com"), regexp(".*app.*mozenda.com.*"), regexp(".*beta.*mozenda.com.*") {
/******************************************/
/*   Global Changes   */
/******************************************/

body {
    background: #000000 !important;
    color: #b9b9b9 !important;
}

.x-grid-item {
    background-color: #000000;
    color: #d0d0d0;
}

section {
    background: #000000 !important;
    color: #b9b9b9 !important;
}

.x-panel-body-default {
    background: #000000;
}

strong {
    color: white;
}

/*Home page custom cell rounding*/

.m-home {
	padding: 0 !important;
}

/*Statistics boxes*/

.x-draw-container {
    filter: invert(100%);
}

/******************************************/
/*   Items (data tables)   */
/******************************************/


/* Data Text area */

.x-grid-view, .x-tree-view {
    background: #000000;
}

.x-grid-with-row-lines .x-grid-item {
    border-color: #333333;
}

.x-grid-with-col-lines .x-grid-cell {
    border-right-color: #333333;
}

.m-grid-details-empty-jobstatistics {
    background-color: #000000;
}

.x-grid-scrollbar-clipper > .x-grid-view, .x-grid-scrollbar-clipper > .x-tree-view {
    background-color: #000000;
}




/* Missing from View Red Text */

.x-column-header.m-column-header-missing-field {
    background: #232323;
    border-right-color: #e4e4e4;
    border-bottom-color: #e4e4e4;
    border-top-color: #e4e4e4;
    color: #ce2929;
}


.x-grid-header-ct,
.x-grid-header-ct > .x-box-inner {
    background: #383636;
}


.m-grid-cell-status.multiple {
    color: #deff7b;
}

.m-grid-cell-status.postponed {
    color: #fab414 !important;
}


/*  Change Markup  */

.m-window-showdiff del {
    background: #3c1c1c;
}

.x-grid-item del {
    background: #3c1c1c;
}

.m-window-showdiff ins {
    background: #273927;
}

.x-grid-item ins {
    background: #273927;
}

.m-window .x-panel-body-default {
    background-color: black;
}



.x-grid-scrollbar-clipper-locked, .x-grid-scrollbar-locked {
    border-color: #c3c3c3;
}

.x-boundlist-item {
    background: #000000;
    color: white;
    border-style: hidden;
}

.x-grid-with-row-lines .x-grid-item.x-grid-item-selected {
    border-color: #000000;
}

.x-grid-with-col-lines .x-grid-item-selected .x-grid-cell {
    border-color: #66696b;
}

.x-boundlist-item-over {
    background: #4a4a4a;
    border-color: #4a4a4a;
}

.x-grid-with-row-lines .x-grid-item:first-child {
    border-top-color: #585858;
}

.x-boundlist {
    border-color: #616161;
}



.m-grid-item-recent {
    background-color: #1e262b;
}

.m-grid-cell-link a:hover {
    color: #e4e4e4;
}

.x-grid-cell-inner a {
    color: #99dded;
}

.x-column-header-over {
    background-color: #000000 !important;
}

.x-column-header-open {
    background-color: #000000;
}

.x-column-header-open .x-column-header-trigger {
    background-color: #5d5d5d;
}

.x-grid-inner-locked .x-column-header {
    border-right-color: #525252;
    border-top-color: #3c3a3a;
    border-bottom-color: #000000;
}

.x-grid-inner-locked .x-column-header-trigger {
    border-left-color: #383434;
    background-color: #585757;
}

.x-grid-inner-locked .x-column-header-open {
    background-color: #585858;
}

.x-grid-scroll-container {
    background-color: #000000;
}

.x-grid-with-row-lines .x-grid-item.x-grid-item-over:first-child {
    border-top-color: #545353;
}

.m-grid-cell-status.done {
    color: #b3b3b3;
}

.m-grid-cell-status.ready {
    color: #b3b3b3;
}




/*Data Table Headers*/

.x-grid-header-ct, .x-grid-header-ct > .x-box-inner {
    background: #232323;
    border-right-color: #3c3c3c;
    border-top-color: #5a5a5a;
    border-bottom-color: #505050 !important;
}

.x-column-header.m-column-header-missing-field {
    border-right-color: #4e4e4e;
    border-bottom-color: #444444;
    border-top-color: #232323;
}

.x-grid-locking-body {
    border-color: #565656;
}






/******************************************/
/*  Selection */
/******************************************/



.m-grid-jobs-list-item-bookmark-selected .m-grid-jobs-list-cell-bookmark-none {
