}

.x-grid-with-row-lines .x-grid-item.x-grid-item-over {
    border-style: solid !important;
    border-color: #000000;
}

.x-grid-with-row-lines .x-grid-item-over + .x-grid-item {
    border-style: solid !important;
    border-color: #000000;
}

.x-grid-with-col-lines .x-grid-item-over .x-grid-cell {
    border-color: #5d6063 !important;
}

.x-grid-item-selected {
    background-color: #2a444c !important;
    color: white !important;
}

.x-grid-item-selected.x-grid-item-over {
    background-color: #526d75 !important;
}

.x-column-header-trigger:hover {
    background-color: #676767;
    border-style: hidden;
}

.x-column-header-trigger {
    border-left-style: hidden;
}

.x-grid-item-over {
    background-color: #262d33 !important;
}

