    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    background-color: #444444;
    border-color: #444444;
}

.m-header .x-btn-default-toolbar-small.selected, .m-header .x-btn-over.x-btn-default-toolbar-small.selected, .m-header .x-btn.x-btn-pressed.x-btn-default-toolbar-small.selected {
