{
    background-color: #444!important;
}

/*Red Warning Backgrounds*/
.boilerplate.metadata
{
    background-color: #150000!important;
}

/*Slightly lighter Warning Backgrounds*/
.mw-parser-output > ul:nth-child(27) > li:nth-child(1) > span:nth-child(2)
{
    background-color: #ff000020!important;
}


/*Dropdowns*/
a.mw-helplink,
#sortBy,
#p-search form,
.rj-search-category-dropdown,
.search-button,
.select2-container
{
    filter: grayscale(1)invert(1)!important;
    z-index: 11;
}

/*Inverted Menus & Text*/
.oo-ui-capsuleMultiselectWidget-handle,
.oo-ui-clippableElement-clippable,
.oo-ui-menuOptionWidget,
.oo-ui-textInputWidget.oo-ui-widget-enabled input,
.oo-ui-textInputWidget.oo-ui-widget-enabled textarea,
.select2-search__field,
.wbb-select .val,
.wysibb-toolbar-btn.wbb-dropdown.wbb-cp,
.wysibb .wysibb-toolbar .wysibb-toolbar-container .wysibb-toolbar-btn span.btn-inner
{
    filter: grayscale(1)invert(1)!important;
}

/*Inverted List Text Inversion (Inverted text back to normal)*/
.rj-search-category-option:hover .select2-container--default .select2-results__option,
.select2-container--default .select2-results__option--highlighted[aria-selected]
{
    color: #000;
}


/*Buttons*/
.pagination ul > li > a
{
    background-color: #222!important;
    transition: .4s!important
}

.pagination ul > li > a:hover
{
    background-color: #444!important;
    transition: .4s!important
}

/*Additional Transitions*/
.category
{
    transition:.4s!important;
}

/*Image Dimming*/
img,
a img,
img.game_cover,
.home-trending ul.tiles li.image-tile .tile-desc,
.rj-game-content-games.rj-grid-12 .rj-game
{
    filter:brightness(75%);
    transition:.4s;
}

a img:hover,
img.game_cover:hover,
ul.tiles li .single-image img:hover,
.home-trending ul.tiles li.image-tile .tile-desc:hover,
.rj-game-content-games.rj-grid-12 .rj-game:hover
{
    filter:brightness(100%);
    transition:.4s;
}



/*Tile Management*/
.fadeoff
{
    background: linear-gradient(to bottom,rgba(56, 56, 56, 0) 0,rgb(0, 0, 0) 100%);
}
ul.tiles.tile-list li.mod-tile .mod-tile-right,
ul.tiles li.image-tile .tile-desc,
.tile-desc
{
    background: linear-gradient(to bottom,rgba(56, 56, 56, 0) 0,rgb(0, 0, 0) 100%)!important;
}

.tiles .fadeoff
{
    background: transparent!important;
}

/*Optional - Ad-Removal*/

#ads,
.ads,
.sidebar-sticky,
.video-ad
{
    height: 0px;
    display: none!important;
}
}

