    width: 100% !important;
}
div.rotatorholder {
    width: 100%;
    height: 0;
    padding-bottom: calc(40% + 10px);
}
div.rotatorbox {
    width: 100%;
    height: 100%;
}
div.rotatorbox div.rotatorcorner {
    height: 0;
    padding-bottom: 40%;
