        padding-bottom: 30%;
    }
    div.rotatorholder {
        padding-bottom: calc(30% + 10px);
    }
}
@media(min-width: 1600px) {
    div.rotatorbox div.rotatorcorner {
        padding-bottom: 20%;
    }
    div.rotatorholder {
        padding-bottom: calc(20% + 10px);
    }
}
#footer {
    padding: 0 10px;
}
#footer div.content {
    width: 100%;
}
}

