/** Список **/
.blogsList {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
	grid-row-gap: 15px;
	grid-column-gap: 10px;
}

.blog {
	display: grid;

	grid-template-areas: 'ltext' 'ltitle' 'linfo' 'ltags';
	overflow-x: hidden;
	overflow-y: hidden;
}

.blog-read, .line {
	display: none;
}

.blog-title {
	grid-area: ltitle;
	font-size: 14px;
	font-weight: 400;
	overflow: hidden;
	line-height: 17px;
	max-height: 34px;
}

.blog-info {
	grid-area: linfo;
	font-size: 12px;
	color: #cacaca;
}

.blog-rating {
	color: #fff;
}

.blog-tags {
	grid-area: ltags;
	font-size: 12px;
	color: #cacaca;
}

.blog-info a, .blog-tags a {
	color: #cacaca !important;
}

.blog .blog-text {
	grid-area: ltext;

	height: 160px;
	padding: 10px;

	font-size: 14px;

	color: #b8b8b8;
	background-color: #111;

