@-moz-document domain("dom.mck-ktits.ru") {
body {
	background: #1c1c1c;
}

a {
    color: #2a52be;
}

.card, .que .history, .userprofile .profile_tree section, .groupinfobox, .well, .navbar, .region_main_settings_menu_proxy, #region-main-box {
	background: #1d1d1d !important;
}


#region-main {
	background: #1d1d1d;	
}

#region-main, #inst32 {
	margin-top: 20px;
}

.content .summary {
	display: none;
}

#nav-drawer {
	background: #1c1c1c;
    overflow: hidden;
}

#nav-drawer:hover {
	overflow: auto;
}

#nav-drawer .list-group li:hover a {
	background: #1a1a1a !important;
}

.list-group-item:last-child {
	background: #1e1e1e;
    border: 1px solid #1b1b1b;
}

.list-group-item.active {
 	background: #1d1d1d;
    color: darkgray;
}

.bg-gray {
	background-color: #1f1f1f !important;
    color: white;
    border: 0;
}

button.btn.nav-link.float-sm-left.mr-1.btn-light.bg-gray:hover {
    background: #1a1a1a !important;
    color: white;
}

.page-header-headings {
	color: white;
}

.attostylesbox.attostylesbox-outline.attostylesbox-outline-blue {
	border: 0;
    border-left: 3px solid #7d9fd3;
    background: #1a1a1a;
}

.moodle-dialogue-base .moodle-dialogue.mod_quiz_preflight_popup .moodle-dialogue-wrap {
	background: #1f1f1f;
}

.pagination li a {
	background: #1c1c1c;
    border: 1px solid #1a1a1a;
}

.course-content ul li.section.main {
	border-bottom: 1px solid #1a1a1a;
}

#page-wrapper #page {
	background: #1c1c1c;
}

.page-item.disabled a {
	background: #1c1c1c !important;
    border: 1px solid #1a1a1a !important;
}

::-webkit-scrollbar-thumb {
    background-image: linear-gradient(to bottom, #00AFEA, #00a859, #FFC829, #ED3437);
    border-radius: 4px;
}

::-webkit-scrollbar-track {
    background: #1f1f1f;
}

::-webkit-scrollbar {
    width: 8px;
}
}

@-moz-document url-prefix("https://zspdabrowka.pl/spdabrowka/") {
.widget_recent_entries{
    background-color: rgba(39, 39, 39, 0.911) !important;
    border-radius: 0% !important;
    border: solid white 2px !important;

}
.widget_text{
    background-color: rgba(39, 39, 39, 0.911) !important;
    border-radius: 0% !important;
    border: solid white 2px !important;

}
a{
    text-decoration: none !important;
    font-family: 'Franklin Gothic Medium', 'Arial Narrow', Arial, sans-serif !important;
    color: rgb(134, 176, 211) !important;
}
body{
    font-family: 'Franklin Gothic Medium', 'Arial Narrow', Arial, sans-serif;
    background-color: rgb(34, 34, 34);
}
.main-navigation ul li ul {
    border-color: rgb(134, 176, 211) !important;
    background-color: #101010 !important; 
}
.main-navigation ul li a, .main-navigation ul li a:visited{
    border-radius: 0%;
    background-color: #101010;
}
#search-wrapper input[type="search"] {
    background-color: #101010;
    border-color: rgb(134, 176, 211);
    color: gainsboro;
    text-shadow: none;
    border-radius: 0%;
}
#sfsi_floater {
    margin-top: 0px;
    margin-left: 0px;
    height: 0%;
    width: 0%;
}
#main-content-wrapper {
    background-color: rgb(27, 27, 27);
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}
#search-wrapper input[type="search"] {
    background-color: #101010 !important;
    border-color: rgb(134, 176, 211) !important;
    color: rgb(134, 176, 211) !important;
    text-shadow: none !important;
}

}

@-moz-document url-prefix("https://ps.pcti.tec.nj.us/guardian"), url-prefix("https://ps.rockyview.ab.ca/guardian") {
html,
body {
    background-color: #4f4b4b;
    color: white;
    height: 0%;
    font-family: Arial, sans-serif;
}
#content-main font {
    display: none;
}
/* hideable black bar on top of page */
#usercontext-bar {
    background: black;
}
/* hide button for above */
#btnContMax {
    background-color: black;
}
/* navigation hide button */
#btnNoNav {
    display: none;
}
/* container titles*/
h1, h2, h3 {
    color: white;
}
/* timeout warning popup - untested*/
#sessiontimeoutwarningBox h2, button {
    color: black !important;
}

/* tabs */
ul.tabs li a {
    background-color: #3d3d3d;
    color: white;
}
/* tabs on mouse hover */
ul.tabs li a:hover {
    background-color: #686868;
}

/* main container outline */
.box-round {
    border: 2px solid #ccc;
    border-radius: 5px;
}
p {
    color: white;
}
p > b {
    color: white;
}
/* Legend -> "Attendance Codes:" */
#legend strong {
    color: white;
}
/* Legend -> attendance codes */
#legend p {
    color: white;
}
/* Navigation -> text */
.psparent #nav-main a {
    color: white;
}
/* Navigation -> School Information*/
#nav-main li.selected,
#nav-main-frame li.selected a {
    background-color: #787779;
}
/* navigation buttons - color hint on left when selected 
#nav-main li::before {
    display: none;
}*/
/* Navigation -> background on mouse hover */
#nav-main li:hover,
#nav-main-frame li:hover,
.psHelpTOCul li:hover,
#nav-main-frame a:hover {
    background-color: #787779;
}
/* Navigation -> outlines */
#nav-main li,
#nav-main-frame li {
    border: 2px solid LightGrey;
}
/* main background */
.group {
    background-color: #4f4b4b;
}
table {
    color: white;
}
table a {
    color: white;
}
table a:hover {
    color: grey;
}
.linkDescList {
    color: black;
}
.linkDescList a {
    color: #195f7d;
}
.linkDescList a:hover {
    color: #00004f;
}
fieldset div {
    background-color: #3d3d3d;
}
fieldset > div:nth-of-type(odd):not(.button-row),
fieldset > div:nth-of-type(even):not(.button-row) {
    background-color: #3d3d3d;
}
#sessiontimeoutwarning {
    background: no-repeat scroll 30px center #7e8388;
    background-color: #7b7b7d;
    color: black;
}
th,
td.th,
table.grid td.th,
table.grid tbody tr:nth-child(odd) > td.th,
table.grid tbody.tbody tr:nth-child(odd) th.th {
    background-color: #5e5d5d;
}
#sessiontimeoutwarningBox h2,
p,
button {
    color: black;
}
ul.tabs li,
ul.tabs li.selected {
    border: 2px solid #ccc;
}
#sessiontimeoutwarningBox h2,
p,
button {
    color: white;
}
#tools {
    color: black;
}
.ui-widget-content {
    color: white;
    background-color: #5e5d5d;
}
}

